import React, { useState, useEffect } from 'react';
// import { useData } from '../../Context/DataContext';//problem here
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

const EditEmployee = () => {
  const { employees, setEmployees } = useData();
const { id } = useParams(); 
  const navigate = useNavigate();
  
  // Initialize form state with default values
  const [form, setForm] = useState({
    nom: '',
    prenom: '',
    email: '',
    poste: ''
  });

  // State for loading and error handling
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Check if the employee exists in the local context
    const employee = employees.find(emp => emp.id_employee.toString() === id);

    if (employee) {
      setForm({
        nom: employee.nom,
        prenom: employee.prenom,
        email: employee.email,
        poste: employee.poste
      });
      setLoading(false); // Set loading to false when the data is found
    } else {
      // If employee is not in local state, fetch from the backend
      const fetchEmployee = async () => {
        try {
          const response = await axios.get(`/api/employees/${id}`);
          const employeeData = response.data;
          if (employeeData) {
            setForm({
              nom: employeeData.nom,
              prenom: employeeData.prenom,
              email: employeeData.email,
              poste: employeeData.poste
            });
            setLoading(false); // Set loading to false after data is fetched
          } else {
            navigate('/employees');
          }
        } catch (error) {
          console.error('Error fetching employee:', error);
          setError('Failed to fetch employee');
          setLoading(false); // Set loading to false even on error
          navigate('/employees');
        }
      };
      fetchEmployee();
    }
  }, [id, employees, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Update employee on the server
      const updatedEmployee = {
        ...form,
        id_employee: parseInt(id) // ensure id is an integer
      };

      // Send PUT request to update the employee
      const response = await axios.put(`/api/employees/${id}`, updatedEmployee);

      // Update the local state with the new employee data
      setEmployees(prevEmployees => 
        prevEmployees.map(emp => emp.id_employee === parseInt(id) ? response.data : emp)
      );

      // Redirect back to the employees list page
      navigate('/employees');
    } catch (error) {
      console.error('Error updating employee:', error);
    }
  };

  // Render loading or error states
  if (loading) {
    return <div>Loading...</div>; // Show loading message while waiting for data
  }

  if (error) {
    return <div>Error: {error}</div>; // Show error message if fetching fails
  }

  return (
    <div className="edit-employee-page">
      <h1 className="text-2xl font-bold mb-6">Edit Employee</h1>
      
      <form onSubmit={handleSubmit} className="max-w-md space-y-4">
        <div>
          <label className="block mb-1">First Name</label>
          <input
            type="text"
            value={form.prenom}
            onChange={(e) => setForm({...form, prenom: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Last Name</label>
          <input
            type="text"
            value={form.nom}
            onChange={(e) => setForm({...form, nom: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Email</label>
          <input
            type="email"
            value={form.email}
            onChange={(e) => setForm({...form, email: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Position</label>
          <input
            type="text"
            value={form.poste}
            onChange={(e) => setForm({...form, poste: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div className="flex space-x-4 pt-4">
          <button type="submit" className="btn-primary">
            Save Changes
          </button>
          <button 
            type="button" 
            onClick={() => navigate('/employees')}
            className="btn-secondary"
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditEmployee;
