import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AssignEquipment = () => {
    const [formData, setFormData] = useState({
        employeeId: '',
        equipmentId: ''
    });
    const [employees, setEmployees] = useState([]);
    const [equipment, setEquipment] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        // Load employees and equipment from localStorage

        const storedEquipment = JSON.parse(localStorage.getItem('equipment') || []);
        setEmployees(storedEmployees);
        setEquipment(storedEquipment.filter(item => item.status === 'Available'));
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        
        // Update equipment status
        const updatedEquipment = JSON.parse(localStorage.getItem('equipment')).map(item => {
            if (item.id === formData.equipmentId) {
                return { ...item, status: 'Assigned' };
            }
            return item;
        });
        
        localStorage.setItem('equipment', JSON.stringify(updatedEquipment));
        
        // Redirect to Equipment page
        navigate('/Equipment');
    };

    return (
        <div className="assign-equipment-page">
            <section className="header-section">
                <h1>Assign Equipment</h1>
                <p>Assign equipment to employees</p>
            </section>
            
            <section className="application-form-section">
                <form onSubmit={handleSubmit}>
                    <select 
                        name="employeeId" 
                        value={formData.employeeId}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Select Employee</option>
                        {employees.map(emp => (
                            <option key={emp.email} value={emp.email}>
                                {emp.name} {emp.surname} ({emp.position})
                            </option>
                        ))}
                    </select>
                    
                    <select 
                        name="equipmentId" 
                        value={formData.equipmentId}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Select Equipment</option>
                        {equipment.map(item => (
                            <option key={item.id} value={item.id}>
                                {item.name} ({item.type})
                            </option>
                        ))}
                    </select>
                    
                    <button type="submit">Assign Equipment</button>
                </form>
            </section>
        </div>
    );
};

export default AssignEquipment;
