import React, { useEffect, useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './navbar.css';

const Navbar = () => {
    const [isScrolled, setIsScrolled] = useState(false);
    const [user, setUser] = useState(null);
    const location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 50);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        }
    }, [location]);

    const handleLogout = () => {
        localStorage.removeItem('user');
        setUser(null);
        navigate('/login');
    };

    return (
        <header className={`navbar ${isScrolled ? 'scrolled' : ''}`}>
            <nav className="nav-container">
                <div className="logo">
                    <Link to="/home">AAVB</Link>
                </div>
                <ul className="nav-links">
                    <li><Link to="/home">Home</Link></li>
                    <li><Link to="/Employees">Employees</Link></li>
                    <li><Link to="/Equipment">Equipment</Link></li>
                    <li><Link to="/Attribution">Attribution</Link></li>
                </ul>
                <div className="nav-icons">
                    {user ? (
                        <>
                            <span className="user-role">ðŸ‘¤ {user.name} ({user.role})</span>
                            <button className="contact-btn" onClick={handleLogout}>LOGOUT</button>
                        </>
                    ) : (
                        <>
                            <Link to="/login"><button className="contact-btn">LOGIN</button></Link>
                            <Link to="/signup"><button className="contact-btn">SIGNUP</button></Link>
                        </>
                    )}
                </div>
            </nav>
        </header>
    );
};

export default Navbar;
