import React from 'react';
import { useData } from '../../Context/DataContext';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';

const Attribution = () => {
  const { attributions, employees, equipment, returnEquipment } = useData();
  const navigate = useNavigate();

  // Group attributions by employee
  const groupedAttributions = attributions.reduce((acc, attribution) => {
    const employeeId = attribution.id_employee;
    if (!acc[employeeId]) {
      acc[employeeId] = {
        employee: employees.find(e => e.id_employee === employeeId),
        equipment: [],
        attributionIds: []
      };
    }
    acc[employeeId].equipment.push(
      equipment.find(e => e.id_materiel === attribution.id_materiel)
    );
    acc[employeeId].attributionIds.push(attribution.id_attribution);
    return acc;
  }, {});

  const handleReturn = async (employeeId, equipmentId) => {
    if (window.confirm("Are you sure you want to return this equipment?")) {
      try {
        // Find the specific attribution for this employee-equipment pair
        const attribution = attributions.find(
          a => a.id_employee === employeeId && a.id_materiel === equipmentId
        );
        if (attribution) {
          await returnEquipment(attribution.id_attribution);
        }
      } catch (error) {
        console.error("Error returning equipment:", error);
      }
    }
  };

  const getEmployeeName = (employee) => {
    return employee ? `${employee.prenom} ${employee.nom}` : 'Unknown Employee';
  };

  return (
    <div className="attribution-page">
      <div className="attribution-header">
        <h1 className="attribution-title">Equipment Assignments</h1>
        <button 
          onClick={() => navigate('/attribution/assign')} 
          className="btn btn-add-employee"
        >
          <FontAwesomeIcon icon={faPlus} /> Add New Attribution
        </button>
      </div>

      <div className="attribution-table-wrapper">
        <table className="attribution-table">
          <thead>
            <tr>
              <th>Employee</th>
              <th>Assigned Equipment</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {Object.values(groupedAttributions).map((group, index) => (
              <tr key={index}>
                <td>
                  {getEmployeeName(group.employee)}
                  <div className="employee-details">
                    {group.employee?.poste && <span>{group.employee.poste}</span>}
                    {group.employee?.email && <span>{group.employee.email}</span>}
                  </div>
                </td>
                <td>
                  <div className="equipment-list">
                    {group.equipment.map((eq, eqIndex) => (
                      <div key={eqIndex} className="equipment-item">
                        <span className="equipment-name">
                          {eq?.type_materiel} - {eq?.marque}
                        </span>
                        <span className="equipment-status">
                          {eq?.etat || 'Unknown status'}
                        </span>
                        <button
                          onClick={() => handleReturn(group.employee.id_employee, eq.id_materiel)}
                          className="btn btn-return"
                        >
                          Return
                        </button>
                      </div>
                    ))}
                  </div>
                </td>
                <td>
                  {group.equipment.some(eq => eq?.etat === 'Attribué') ? (
                    <span className="status-active">Active Assignments</span>
                  ) : (
                    <span className="status-completed">All Returned</span>
                  )}
                </td>
                <td>
                  <button 
                    onClick={() => navigate(`/attribution/assign?employeeId=${group.employee.id_employee}`)}
                    className="btn btn-assign-more"
                  >
                    <FontAwesomeIcon icon={faPlus} /> Assign More
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Attribution;
