import React from 'react';
import { useData } from '../../Context/DataContext';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';

const Attribution = () => {
  const { attributions, employees, equipment, setAttributions } = useData();
  const navigate = useNavigate();

  const handleReturn = (id) => {
    const updatedAttributions = attributions.map(attr => {
      if (attr.id_attribution === id) {
        return { ...attr, date_retour: new Date().toISOString().split('T')[0] };
      }
      return attr;
    });
    setAttributions(updatedAttributions);
  };

  const handleEdit = (id) => {
    // Navigate to an edit page with the attribution ID
    navigate(`/attribution/edit/${id}`);
  };

  const handleDelete = (id) => {
    const updatedAttributions = attributions.filter(attr => attr.id_attribution !== id);
    setAttributions(updatedAttributions);
  };

  const getEmployeeName = (id) => {
    const emp = employees.find(e => e.id_employee.toString() === id.toString());
    return emp ? `${emp.prenom} ${emp.nom}` : 'Unknown';
  };

  const getEquipmentDetails = (id) => {
    const eq = equipment.find(e => e.id_materiel.toString() === id.toString());
    return eq ? `${eq.type_materiel} (${eq.marque})` : 'Unknown';
  };

  return (
    <div className="attribution-page">
      <div className="attribution-header">
        <h1 className="attribution-title">Equipment Assignments</h1>
        <button 
          onClick={() => navigate('/attribution/assign')} 
          className="btn btn-add-employee"
        >
          <FontAwesomeIcon icon={faPlus} /> Add New Attribution
        </button>
      </div>

      <div className="attribution-table-wrapper">
        <table className="attribution-table">
          <thead>
            <tr>
              <th>Employee</th>
              <th>Equipment</th>
              <th>Assignment Date</th>
              <th>Return Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {attributions.map(attr => (
              <tr key={attr.id_attribution}>
                <td>{getEmployeeName(attr.id_employee)}</td>
                <td>{getEquipmentDetails(attr.id_materiel)}</td>
                <td>{attr.date_attribution}</td>
                <td>{attr.date_retour || 'Not returned'}</td>
                <td className="attribution-actions">
                  {!attr.date_retour && (
                    <button 
                      onClick={() => handleReturn(attr.id_attribution)}
                      className="btn btn-edit"
                    >
                      Mark Returned
                    </button>
                    
                  )}
                  <button 
                    onClick={() => handleEdit(attr.id_attribution)}
                    className="zzzz"
                  >
                    <FontAwesomeIcon icon={faEdit} /> 
                  </button>
                  <button 
                    onClick={() => handleDelete(attr.id_attribution)}
                    className="btn btn-delete"
                  >
                    <FontAwesomeIcon icon={faTrash} /> 
                  </button>
                </td>
                
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <section className="faq-section">
        <h2 className="faq-title">Assignment Management FAQ</h2>
        <div className="faq-content">
          <div>
            <h3 className="faq-question">How to assign equipment?</h3>
            <p className="faq-answer">Click on "Assign Equipment" button and select employee and equipment.</p>
          </div>
          <div>
            <h3 className="faq-question">How to mark equipment as returned?</h3>
            <p className="faq-answer">Click "Mark Returned" button next to the assignment.</p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Attribution;
