import React from 'react';
import { useData } from '../../Context/DataContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheck, faTimes } from '@fortawesome/free-solid-svg-icons';

const Attribution = () => {
  const { attributions, employees, equipment } = useData();

  // Assuming we have user information available
  const userId = 1; // Replace with logged-in user ID (this could be retrieved from context or state)

  // Filter attributions for the logged-in user
  const userAttributions = attributions.filter(attribution => attribution.id_employee === userId);

  const groupedAttributions = userAttributions.reduce((acc, attribution) => {
    const equipmentId = attribution.id_materiel;

    if (!acc[equipmentId]) {
      const eq = equipment.find(e => e.id_materiel === equipmentId);
      acc[equipmentId] = {
        equipment: eq,
        attribution,
        isReturned: !!attribution.date_retour
      };
    }

    return acc;
  }, {});

  return (
    <div className="attribution-page">
      <div className="attribution-header">
        <h1>Your Equipment Assignments</h1>
      </div>

      <div className="attribution-table-wrapper">
        <table className="attribution-table">
          <thead>
            <tr>
              <th>Equipment</th>
              <th>Assigned On</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {Object.values(groupedAttributions).map((assignment, index) => (
              <tr key={index} className={assignment.isReturned ? 'returned' : 'active'}>
                <td>
                  {assignment.equipment?.type_materiel} ({assignment.equipment?.marque})
                </td>
                <td>
                  {new Date(assignment.attribution.date_attribution).toLocaleDateString()}
                </td>
                <td>
                  {assignment.isReturned ? (
                    <span className="status-badge returned">
                      <FontAwesomeIcon icon={faCheck} /> Returned
                    </span>
                  ) : (
                    <span className="status-badge active">
                      <FontAwesomeIcon icon={faTimes} /> Assigned
                    </span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Attribution;
