import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Main Admin View
const AdminDashboard = () => {
  const [employees, setEmployees] = useState([]);
  const [equipment, setEquipment] = useState([]);
  const [attributions, setAttributions] = useState([]);

  // State for new/updated form inputs
  const [formData, setFormData] = useState({
    employee: { id_employee: '', prenom: '', nom: '', email: '', poste: '' },
    equipment: { id_materiel: '', type_materiel: '', marque: '', modele: '', etat: '' },
    attribution: { id_employee: '', id_materiel: '', date_attribution: '', date_retour: '' }
  });

  // Fetch data on component mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [employeeRes, equipmentRes, attributionRes] = await Promise.all([
          axios.get('/api/employees'),
          axios.get('/api/equipment'),
          axios.get('/api/attributions')
        ]);
        setEmployees(employeeRes.data);
        setEquipment(equipmentRes.data);
        setAttributions(attributionRes.data);
      } catch (error) {
        console.error('Error fetching data', error);
      }
    };
    fetchData();
  }, []);

  // Handle form input change for all sections
  const handleChange = (section, e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [section]: { ...prevState[section], [name]: value }
    }));
  };

  // Handle form submit (Add/Update) for all sections
  const handleSubmit = async (section, e) => {
    e.preventDefault();

    try {
      if (section === 'employee') {
        const response = formData.employee.id_employee
          ? await axios.put(`/api/employees/${formData.employee.id_employee}`, formData.employee)
          : await axios.post('/api/employees', formData.employee);
        setEmployees(prev => [...prev, response.data]);
      }

      if (section === 'equipment') {
        const response = formData.equipment.id_materiel
          ? await axios.put(`/api/equipment/${formData.equipment.id_materiel}`, formData.equipment)
          : await axios.post('/api/equipment', formData.equipment);
        setEquipment(prev => [...prev, response.data]);
      }

      if (section === 'attribution') {
        const response = await axios.post('/api/attributions', formData.attribution);
        setAttributions(prev => [...prev, response.data]);
      }
    } catch (error) {
      console.error(`Error adding/updating ${section}`, error);
    }

    // Clear form data after submission
    setFormData({ employee: {}, equipment: {}, attribution: {} });
  };

  // Handle delete for all sections
  const handleDelete = async (section, id) => {
    try {
      if (section === 'employee') {
        await axios.delete(`/api/employees/${id}`);
        setEmployees(prev => prev.filter(emp => emp.id_employee !== id));
      }

      if (section === 'equipment') {
        await axios.delete(`/api/equipment/${id}`);
        setEquipment(prev => prev.filter(item => item.id_materiel !== id));
      }

      if (section === 'attribution') {
        await axios.delete(`/api/attributions/${id}`);
        setAttributions(prev => prev.filter(attrib => attrib.id_attribution !== id));
      }
    } catch (error) {
      console.error(`Error deleting ${section}`, error);
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <div>
        <h2>Manage Employees</h2>
        <form onSubmit={(e) => handleSubmit('employee', e)}>
          {/* Form inputs for employee */}
          <input
            type="text"
            name="prenom"
            value={formData.employee.prenom}
            onChange={(e) => handleChange('employee', e)}
            placeholder="First Name"
          />
          <input
            type="text"
            name="nom"
            value={formData.employee.nom}
            onChange={(e) => handleChange('employee', e)}
            placeholder="Last Name"
          />
          <input
            type="email"
            name="email"
            value={formData.employee.email}
            onChange={(e) => handleChange('employee', e)}
            placeholder="Email"
          />
          <input
            type="text"
            name="poste"
            value={formData.employee.poste}
            onChange={(e) => handleChange('employee', e)}
            placeholder="Position"
          />
          <button type="submit">{formData.employee.id_employee ? 'Update' : 'Add'} Employee</button>
        </form>

        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Position</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {employees.map(emp => (
              <tr key={emp.id_employee}>
                <td>{emp.prenom} {emp.nom}</td>
                <td>{emp.email}</td>
                <td>{emp.poste}</td>
                <td>
                  <button onClick={() => setFormData({ ...formData, employee: emp })}>Edit</button>
                  <button onClick={() => handleDelete('employee', emp.id_employee)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div>
        <h2>Manage Equipment</h2>
        <form onSubmit={(e) => handleSubmit('equipment', e)}>
          {/* Form inputs for equipment */}
          <input
            type="text"
            name="type_materiel"
            value={formData.equipment.type_materiel}
            onChange={(e) => handleChange('equipment', e)}
            placeholder="Equipment Type"
          />
          <input
            type="text"
            name="marque"
            value={formData.equipment.marque}
            onChange={(e) => handleChange('equipment', e)}
            placeholder="Brand"
          />
          <input
            type="text"
            name="modele"
            value={formData.equipment.modele}
            onChange={(e) => handleChange('equipment', e)}
            placeholder="Model"
          />
          <input
            type="text"
            name="etat"
            value={formData.equipment.etat}
            onChange={(e) => handleChange('equipment', e)}
            placeholder="Condition"
          />
          <button type="submit">{formData.equipment.id_materiel ? 'Update' : 'Add'} Equipment</button>
        </form>

        <table>
          <thead>
            <tr>
              <th>Type</th>
              <th>Brand</th>
              <th>Model</th>
              <th>Condition</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {equipment.map(item => (
              <tr key={item.id_materiel}>
                <td>{item.type_materiel}</td>
                <td>{item.marque}</td>
                <td>{item.modele}</td>
                <td>{item.etat}</td>
                <td>
                  <button onClick={() => setFormData({ ...formData, equipment: item })}>Edit</button>
                  <button onClick={() => handleDelete('equipment', item.id_materiel)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div>
        <h2>Manage Attributions</h2>
        <form onSubmit={(e) => handleSubmit('attribution', e)}>
          {/* Form inputs for attribution */}
          <select
            name="id_employee"
            value={formData.attribution.id_employee}
            onChange={(e) => handleChange('attribution', e)}
          >
            <option value="">Select Employee</option>
            {employees.map(emp => (
              <option key={emp.id_employee} value={emp.id_employee}>
                {emp.prenom} {emp.nom}
              </option>
            ))}
          </select>
          <select
            name="id_materiel"
            value={formData.attribution.id_materiel}
            onChange={(e) => handleChange('attribution', e)}
          >
            <option value="">Select Equipment</option>
            {equipment.map(item => (
              <option key={item.id_materiel} value={item.id_materiel}>
                {item.type_materiel} ({item.marque})
              </option>
            ))}
          </select>
          <input
            type="date"
            name="date_attribution"
            value={formData.attribution.date_attribution}
            onChange={(e) => handleChange('attribution', e)}
          />
          <input
            type="date"
            name="date_retour"
            value={formData.attribution.date_retour}
            onChange={(e) => handleChange('attribution', e)}
          />
          <button type="submit">Assign Equipment</button>
        </form>

        <table>
          <thead>
            <tr>
              <th>Employee</th>
              <th>Equipment</th>
              <th>Assignment Date</th>
              <th>Return Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {attributions.map(attrib => (
              <tr key={attrib.id_attribution}>
                <td>{attrib.id_employee}</td>
                <td>{attrib.id_materiel}</td>
                <td>{attrib.date_attribution}</td>
                <td>{attrib.date_retour || 'Not Returned'}</td>
                <td>
                  <button onClick={() => handleDelete('attribution', attrib.id_attribution)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
