import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Equipment = () => {
    const navigate = useNavigate();
    const [equipment, setEquipment] = useState([]);

    useEffect(() => {
        const storedEquipment = JSON.parse(localStorage.getItem('materiel') || '[]');
        setEquipment(storedEquipment);
    }, []);

    return (
        <div className="equipment-page">
            <section className="header-section">
                <h1>Matériel</h1>
                <p>Gestion du matériel de l'entreprise</p>
                <div className="button-group">
                    <button onClick={() => navigate('/AddEquipment')}>Ajouter Matériel</button>
                    <button onClick={() => navigate('/AssignEquipment')}>Attribuer Matériel</button>
                </div>
            </section>
            
            <section className="inventory-section">
                <h2>Inventaire</h2>
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Type</th>
                            <th>Marque</th>
                            <th>Modèle</th>
                            <th>État</th>
                        </tr>
                    </thead>
                    <tbody>
                        {equipment.map((item) => (
                            <tr key={item.id_materiel}>
                                <td>{item.id_materiel}</td>
                                <td>{item.type_materiel}</td>
                                <td>{item.marque}</td>
                                <td>{item.modele}</td>
                                <td>{item.etat}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </section>
        </div>
    );
};

export default Equipment;
