import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faEdit, faTrash, faLaptop } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';


const Equipment = () => {
  const navigate = useNavigate();
  const [equipment, setEquipment] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
const [attributions, setAttributions] = useState([]); // State to hold attributions

  useEffect(() => {
    const fetchEquipment = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/equipment');
        setEquipment(response.data);
        setError(null);
      } catch (err) {
        setError(err.message);
        console.error("Error fetching equipment:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchEquipment();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this equipment?")) {
      try {
        await axios.delete(`http://localhost:5000/api/equipment/${id}`);
        setEquipment(equipment.filter(item => item.id_materiel !== id));
      } catch (error) {
        console.error("Error deleting equipment:", error);
      }
    }
  };
  const handleReturn = async (id_attribution) => {
  try {
    // Make the API call to return the item
    const response = await axios.put(`http://localhost:5000/api/attribution/return/${id_attribution}`);

    // Show a success message or update the UI accordingly
    if (response.status === 200) {
      alert('Item returned successfully!');
      // Optionally, update your state to reflect the change
      // For example, re-fetch the attributions or update the local state
      setAttributions(prevAttributions => 
        prevAttributions.map(item =>
          item.id_attribution === id_attribution ? { ...item, status: 'Returned' } : item
        )
      );
    } else {
      alert('Failed to return item');
    }
  } catch (error) {
    console.error('Error returning item:', error);
    alert('There was an error processing the return');
  }
};


  if (loading) return <div className="loading">Loading equipment...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="equipment-container">
      <div className="equipment-header">
        <h1>Equipment Management</h1>
        <button 
          onClick={() => navigate('/equipment/add')}
          className="btn-add"
        >
          <FontAwesomeIcon icon={faPlus} /> Add Equipment
        </button>
      </div>

      {equipment.length === 0 ? (
        <div className="empty-state">
          <p>No equipment found. Add your first equipment to get started.</p>
          <button 
            onClick={() => navigate('/equipment/add')}
            className="btn-add"
          >
            <FontAwesomeIcon icon={faPlus} /> Add Equipment
          </button>
        </div>
      ) : (
        <table className="equipment-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Type</th>
              <th>Brand</th>
              <th>Model</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {equipment.map((item) => {
  return (
    <tr key={item.id_materiel}>
      <td>{item.id_materiel}</td>
      <td>{item.type_materiel}</td>
      <td>{item.marque}</td>
      <td>{item.modele}</td>
      <td>
        <span className={`status status-${item.etat.toLowerCase().replace('é', 'e').replace(' ', '-')}`}>
          {item.etat}
        </span>
      </td>
      <td className="actions">
        <button 
          onClick={() => handleReturn(item.id_attribution)} 
          className="btn-return"
          title="Return"
        >
          Return
        </button>
        <button 
          onClick={() => navigate(`/attribution/edit/${item.id_attribution}`)}
          className="btn-edit"
          title="Edit"
        >
          <FontAwesomeIcon icon={faEdit} />
        </button>
        <button 
          onClick={() => handleDelete(item.id_materiel)}
          className="btn-delete"
          title="Delete"
        >
          <FontAwesomeIcon icon={faTrash} />
        </button>
        <button 
          onClick={() => navigate(`/attribution/assign?equipmentId=${item.id_materiel}`)}
          className="btn-assign"
          title="Assign"
        >
          <FontAwesomeIcon icon={faLaptop} />
        </button>
      </td>
    </tr>
  );
})}

          </tbody>
        </table>
      )}
    </div>
  );
};

export default Equipment;
