import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AssignEquipment = () => {
    const [formData, setFormData] = useState({
        id_employee: '',
        id_materiel: '',
        date_attribution: new Date().toISOString().split('T')[0],
        date_retour: ''
    });
    const [employees, setEmployees] = useState([]);
    const [availableEquipment, setAvailableEquipment] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        // Load employees and available equipment
        const storedEmployees = JSON.parse(localStorage.getItem('employes') || []);
        const storedEquipment = JSON.parse(localStorage.getItem('materiel') || []);
        
        setEmployees(storedEmployees);
        setAvailableEquipment(storedEquipment.filter(
            item => item.etat === 'Disponible'
        ));
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        
        // Update equipment status
        const updatedEquipment = JSON.parse(localStorage.getItem('materiel')).map(item => {
            if (item.id_materiel === formData.id_materiel) {
                return { ...item, etat: 'Attribué' };
            }
            return item;
        });
        
        // Save assignment
        const assignments = JSON.parse(localStorage.getItem('attributions') || []);
        assignments.push(formData);
        
        localStorage.setItem('materiel', JSON.stringify(updatedEquipment));
        localStorage.setItem('attributions', JSON.stringify(assignments));
        navigate('/Equipment');
    };

    return (
        <div className="assign-equipment-page">
            <section className="header-section">
                <h1>Attribuer Matériel</h1>
            </section>
            
            <form onSubmit={handleSubmit}>
                <select
                    name="id_employee"
                    value={formData.id_employee}
                    onChange={handleChange}
                    required
                >
                    <option value="">Sélectionner un employé</option>
                    {employees.map(emp => (
                        <option key={emp.id_employee} value={emp.id_employee}>
                            {emp.prenom} {emp.nom} ({emp.poste})
                        </option>
                    ))}
                </select>
                
                <select
                    name="id_materiel"
                    value={formData.id_materiel}
                    onChange={handleChange}
                    required
                >
                    <option value="">Sélectionner un matériel</option>
                    {availableEquipment.map(item => (
                        <option key={item.id_materiel} value={item.id_materiel}>
                            {item.type_materiel} {item.marque} ({item.modele})
                        </option>
                    ))}
                </select>
                
                <input
                    type="date"
                    name="date_attribution"
                    value={formData.date_attribution}
                    onChange={handleChange}
                    required
                />
                <label>date de</label>
                <input
                    type="date"
                    name="date_retour"
                    value={formData.date_retour}
                    onChange={handleChange}
                    placeholder="Date de retour (optionnel)"
                />
                
                <button type="submit">Attribuer</button>
            </form>
        </div>
    );
};

export default AssignEquipment;
