import React, { useState, useEffect } from 'react';
import { useData } from '../../context/DataContext';
import { useNavigate, useLocation } from 'react-router-dom';

const AssignEquipment = () => {
  const { employees, equipment, attributions, setAttributions } = useData();
  const navigate = useNavigate();
  const location = useLocation();
  
  const queryParams = new URLSearchParams(location.search);
  const employeeId = queryParams.get('employeeId');

  const [form, setForm] = useState({
    id_employee: employeeId || '',
    id_materiel: '',
    date_attribution: new Date().toISOString().split('T')[0],
    date_retour: ''
  });

  const availableEquipment = equipment.filter(item => 
    item.etat === 'Disponible' || 
    (form.id_materiel && item.id_materiel.toString() === form.id_materiel)
  );

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Update equipment status
    const updatedEquipment = equipment.map(item => 
      item.id_materiel.toString() === form.id_materiel 
        ? { ...item, etat: 'AttribuÃ©' } 
        : item
    );
    
    // Add new attribution
    const newAttribution = {
      ...form,
      id_attribution: attributions.length > 0 ? Math.max(...attributions.map(a => a.id_attribution)) + 1 : 1
    };
    
    setAttributions([...attributions, newAttribution]);
    navigate('/attribution');
  };

  return (
    <div className="assign-equipment-page">
      <h1 className="text-2xl font-bold mb-6">Assign Equipment</h1>
      
      <form onSubmit={handleSubmit} className="max-w-md space-y-4">
        <div>
          <label className="block mb-1">Employee</label>
          <select
            value={form.id_employee}
            onChange={(e) => setForm({...form, id_employee: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          >
            <option value="">Select Employee</option>
            {employees.map(emp => (
              <option key={emp.id_employee} value={emp.id_employee}>
                {emp.prenom} {emp.nom}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block mb-1">Equipment</label>
          <select
            value={form.id_materiel}
            onChange={(e) => setForm({...form, id_materiel: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          >
            <option value="">Select Equipment</option>
            {availableEquipment.map(item => (
              <option key={item.id_materiel} value={item.id_materiel}>
                {item.type_materiel} - {item.marque} {item.modele} ({item.etat})
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block mb-1">Assignment Date</label>
          <input
            type="date"
            value={form.date_attribution}
            onChange={(e) => setForm({...form, date_attribution: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Return Date (Optional)</label>
          <input
            type="date"
            value={form.date_retour}
            onChange={(e) => setForm({...form, date_retour: e.target.value})}
            className="w-full px-3 py-2 border rounded"
          />
        </div>

        <div className="flex space-x-4 pt-4">
          <button type="submit" className="btn-primary">
            Assign Equipment
          </button>
          <button 
            type="button" 
            onClick={() => navigate('/attribution')}
            className="btn-secondary"
          >
            Cancel
          </button>
        </div>
      </form>

      {/* FAQ Section */}
      <section className="mt-12">
        <h2 className="text-xl font-bold mb-4">Assignment FAQ</h2>
        <div className="space-y-4">
          <div>
            <h3 className="font-semibold">How to assign equipment?</h3>
            <p>Select an employee and available equipment, then set the assignment date.</p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default AssignEquipment;
