import React, { useEffect, useState } from 'react';  // Importing useState and useEffect
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faCheck, faTimes, faTrash } from '@fortawesome/free-solid-svg-icons';

const Attribution = () => {
  const navigate = useNavigate();
  const [attributions, setAttributions] = useState([]);
  const [groupedAttributions, setGroupedAttributions] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAttributions = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/attributions');
        const attributionData = response.data;

        const employeesResponse = await axios.get('http://localhost:5000/api/employees');
        const equipmentResponse = await axios.get('http://localhost:5000/api/equipment');

        const attributionsWithDetails = attributionData.map(attribution => {
          const employee = employeesResponse.data.find(e => e.id_employee === attribution.id_employee);
          const equipment = equipmentResponse.data.find(eq => eq.id_materiel === attribution.id_materiel);

          return {
            ...attribution,
            employee,
            equipment
          };
        });

        setAttributions(attributionsWithDetails);
        groupAttributions(attributionsWithDetails);
        setError(null);
      } catch (err) {
        setError(err.message);
        console.error("Error fetching attributions:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchAttributions();
  }, []);

  const groupAttributions = (attributions) => {
    const grouped = attributions.reduce((acc, attribution) => {
      const { id_employee } = attribution;

      if (!acc[id_employee]) {
        acc[id_employee] = {
          employee: attribution.employee,
          assignments: []
        };
      }

      acc[id_employee].assignments.push(attribution);
      return acc;
    }, {});

    setGroupedAttributions(grouped);
  };

const handleReturn = async (id_attribution) => {
  try {
    // Make the API call to return the item
    const response = await axios.put(`http://localhost:5000/api/attribution/return/${id_attribution}`);

    // Show a success message or update the UI accordingly
    if (response.status === 200) {
      alert('Item returned successfully!');
      // Optionally, update your state to reflect the change
      // For example, re-fetch the attributions or update the local state
      setAttributions(prevAttributions => 
        prevAttributions.map(item =>
          item.id_attribution === id_attribution ? { ...item, status: 'Returned' } : item
        )
      );
    } else {
      alert('Failed to return item');
    }
  } catch (error) {
    console.error('Error returning item:', error);
    alert('There was an error processing the return');
  }
};





  const handleDelete = async (attributionId) => {
    try {
      await axios.delete(`http://localhost:5000/api/attributions/${attributionId}`);
      setAttributions(prevAttributions =>
        prevAttributions.filter(attribution => attribution.id_attribution !== attributionId)
      );
      groupAttributions(attributions);
    } catch (err) {
      setError('Failed to delete attribution. Please try again later.');
      console.error('Error deleting attribution:', err);
    }
  };

  const handleAssignMore = (employeeId) => {
    navigate(`/attribution/assign/${employeeId}`);
  };

  if (loading) return <div className="loading">Loading attributions...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="attribution-page">
      <div className="attribution-header">
        <h1>Equipment Assignments</h1>
        <button 
          onClick={() => navigate('/attribution/assign')}
          className="btn-primary"
        >
          <FontAwesomeIcon icon={faPlus} /> New Assignment
        </button>
      </div>

      <div className="attribution-table-wrapper">
        <table className="attribution-table">
          <thead>
            <tr>
              <th>Employee</th>
              <th>Equipment</th>
              <th>Assigned On</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {Object.values(groupedAttributions).map((group, index) => (
              <React.Fragment key={index}>
                {group.assignments.map((assignment, idx) => (
                  <tr 
                    key={`${index}-${idx}`}
                    className={`${assignment.isReturned ? 'returned' : 'active'} ${assignment.isDuplicate ? 'duplicate' : ''}`}
                  >
                    {idx === 0 && (
                      <td rowSpan={group.assignments.length} className="employee-cell">
                        <div className="employee-name">
                          <strong>{group.employee?.prenom} {group.employee?.nom}</strong>
                        </div>
                        <div className="employee-details">
                          {group.employee?.poste && (
                            <div className="employee-poste">{group.employee.poste}</div>
                          )}
                          {group.employee?.email && (
                            <div className="employee-email">{group.employee.email}</div>
                          )}
                        </div>
                        <button 
                          onClick={() => handleAssignMore(group.employee.id_employee)}
                          className="btn-assign-more"
                          disabled={!group.employee}
                        >
                          <FontAwesomeIcon icon={faPlus} /> Assign More
                        </button>
                      </td>
                    )}
                    <td>
                      {assignment.equipment?.type_materiel} ({assignment.equipment?.marque})
                      {assignment.isDuplicate && (
                        <span className="duplicate-warning"> (Already assigned!)</span>
                      )}
                    </td>
                    <td>
                      {assignment.attribution?.date_attribution ? 
                        new Date(assignment.attribution.date_attribution).toLocaleDateString() : 'N/A'}
                    </td>
                    <td>
                      {assignment.isReturned ? (
                        <span className="status-badge returned">
                          <FontAwesomeIcon icon={faCheck} /> Returned
                        </span>
                      ) : (
                        <span className="status-badge active">
                          <FontAwesomeIcon icon={faTimes} /> Assigned
                        </span>
                      )}
                    </td>
                    <td className="actions-cell">
                      {!assignment.isReturned ? (
                        <button 
  onClick={() => handleReturn(item.id_attribution)} 
  className="btn-return"
  title="Return"
>
  Return
</button>

                      ) : (
                        <button
                          onClick={() => handleDelete(assignment.attribution.id_attribution)}
                          className="btn-delete"
                          title="Delete record"
                        >
                          <FontAwesomeIcon icon={faTrash} /> Delete
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Attribution;
