import React, { useState, useEffect } from 'react';
import './footer.css';
import image0 from '../assets/image_0.jpg'; // Ensure the image is in src/assets/

const Footer = () => {
  // State for showing more hours
  const [showAll, setShowAll] = useState(false);

  // Function to get today's day and hide/show based on it
  const getTodayHours = () => {
    const today = new Date().getDay();
    const days = [
      'Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'
    ];
    return days[today];
  };

  const today = getTodayHours();

  // Handle the "Show More" button click
  const handleShowMore = () => {
    setShowAll(true);
  };

  return (
    <footer className="footer">
      <div className="footer-content">
        {/* Logo */}
        <div className="footer-logo">
          <img src={image0} alt="AAVB Company Logo" />
        </div>

        {/* Company Info */}
        <div className="footer-info">
          <h4>AAVB Company</h4>
          <p>
            Empowering businesses with cutting-edge solutions. Our platform simplifies
            employee and equipment management, ensuring efficiency and productivity.
          </p>
          <ul>
            <li>üìç Address: Address</li>
            <li>üìß Email: contact@aavb.com</li>
            <li>üìû Phone: +212 53 72 16 50 0</li>
            <li>
              üïí {today}: 08:30 AM - 4:30 PM
            </li>

            {showAll && (
              <>
                <li>üïí Monday: 08:30 AM - 4:30 PM</li>
                <li>üïí Tuesday: 08:30 AM - 4:30 PM</li>
                <li>üïí Wednesday: 08:30 AM - 4:30 PM</li>
                <li>üïí Thursday: 08:30 AM - 4:30 PM</li>
                <li>üïí Friday: 08:30 AM - 4:30 PM</li>
                <li>üïí Saturday: Closed</li>
                <li>üïí Sunday: Closed</li>
              </>
            )}

            <li>üåê <a href="http://www.bouregreg.com/" target="_blank" rel="noopener noreferrer">www.bouregreg.com</a></li>
          </ul>

          {/* Show More Button */}
          {!showAll && (
            <button onClick={handleShowMore} className="show-more-btn">
              Show More
            </button>
          )}
        </div>
      </div>

      {/* Copyright */}
      <div className="footer-rights">
        <p>¬© 2025 AAVB Company. All rights reserved.</p>
      </div>
    </footer>
  );
};

export default Footer;
