import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AddEquipment = () => {
    const [formData, setFormData] = useState({
        id_materiel: Date.now().toString(),
        type_materiel: '',
        marque: '',
        etat: 'Disponible'
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const storedEquipment = JSON.parse(localStorage.getItem('materiel') || '[]');
        storedEquipment.push(formData);
        localStorage.setItem('materiel', JSON.stringify(storedEquipment));
        navigate('/Equipment');
    };

    return (
        <div className="add-equipment-page">
            <h1>Add New Equipment</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Type:
                    <input type="text" name="type_materiel" value={formData.type_materiel} onChange={handleChange} required />
                </label>

                <label>
                    Brand:
                    <input type="text" name="marque" value={formData.marque} onChange={handleChange} required />
                </label>

                <label>
                    Status:
                    <select name="etat" value={formData.etat} onChange={handleChange} required>
                        <option value="Disponible">Disponible</option>
                        <option value="Attribué">Attribué</option>
                        <option value="Réparé">Réparé</option>
                        <option value="Hors service">Hors service</option>
                    </select>
                </label>

                <button type="submit">Add Equipment</button>
            </form>
        </div>
    );
};

export default AddEquipment;
