import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AssignEquipment = () => {
    const [formData, setFormData] = useState({
        id_employee: '',
        id_materiel: '',
        date_attribution: new Date().toISOString().split('T')[0],
        date_retour: ''
    });
    const [employees, setEmployees] = useState([]);
    const [availableEquipment, setAvailableEquipment] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        // Load data with fallbacks
        const storedEmployees = localStorage.getItem('employes') || 
                              localStorage.getItem('employees') || 
                              '[]';
        const storedEquipment = localStorage.getItem('materiel') || 
                               localStorage.getItem('equipment') || 
                               '[]';

        setEmployees(JSON.parse(storedEmployees));
        
        // Filter available equipment (check both French and English status fields)
        const equipment = JSON.parse(storedEquipment);
        setAvailableEquipment(
            equipment.filter(item => 
                (item.etat === 'Disponible' || item.status === 'Available') &&
                !item.id_employee // Not already assigned
            )
        );
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        
        // Update equipment assignment
        const equipment = JSON.parse(localStorage.getItem('materiel') || '[]');
        const updatedEquipment = equipment.map(item => {
            if (item.id_materiel === formData.id_materiel) {
                return { 
                    ...item, 
                    etat: 'Attribué',
                    id_employee: formData.id_employee
                };
            }
            return item;
        });

        // Save assignment record
        const assignments = JSON.parse(localStorage.getItem('attributions') || '[]');
        assignments.push({
            ...formData,
            employee_name: employees.find(e => e.id_employee == formData.id_employee)?.prenom + ' ' + 
                          employees.find(e => e.id_employee == formData.id_employee)?.nom,
            equipment_name: availableEquipment.find(e => e.id_materiel == formData.id_materiel)?.type_materiel
        });

        // Update storage
        localStorage.setItem('materiel', JSON.stringify(updatedEquipment));
        localStorage.setItem('attributions', JSON.stringify(assignments));
        localStorage.setItem('employes', JSON.stringify([
    { id_employee: "1", prenom: "John", nom: "Doe" },
    { id_employee: "2", prenom: "Jane", nom: "Smith" }
]));
        
        navigate('/Equipment');
    };

    return (
        <div className="assign-equipment-page">
            <h1>Assign Equipment</h1>
            
            <form onSubmit={handleSubmit}>
                <label>
                    Employee:
                    <select
                        name="id_employee"
                        value={formData.id_employee}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Select Employee</option>
                        {employees.length > 0 ? (
    employees.map(emp => (
        <option key={emp.id_employee} value={emp.id_employee}>
            {emp.prenom || emp.firstName} {emp.nom || emp.lastName}
        </option>
    ))
) : (
    <option disabled>No employees found</option>
)}

                        
                    </select>
                </label>

                <label>
                    Equipment:
                    <select
                        name="id_materiel"
                        value={formData.id_materiel}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Select Equipment</option>
                        {availableEquipment.map(item => (
                            <option key={item.id_materiel} value={item.id_materiel}>
                                {item.type_materiel || item.type} - 
                                {item.marque || item.brand} (
                                {item.etat || item.status})
                            </option>
                        ))}
                    </select>
                </label>

                <label>
                    Assignment Date:
                    <input
                        type="date"
                        name="date_attribution"
                        value={formData.date_attribution}
                        onChange={handleChange}
                        required
                    />
                </label>

                <label>
                    Return Date (optional):
                    <input
                        type="date"
                        name="date_retour"
                        value={formData.date_retour}
                        onChange={handleChange}
                    />
                </label>

                <button type="submit">Assign Equipment</button>
            </form>
        </div>
    );
};

export default AssignEquipment;
