import React from 'react';
import { useData } from '../../Context/DataContext';
import { Link, useNavigate } from 'react-router-dom';

const Equipment = () => {
  const { equipment, setEquipment } = useData();
  const navigate = useNavigate();

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this equipment?")) {
      setEquipment(equipment.filter(item => item.id_materiel !== id));
    }
  };

  return (
    <div className="equipment-page">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-2xl font-bold">Equipment</h1>
        <Link to="/equipment/add" className="btn-primary">
          Add New Equipment
        </Link>
      </div>

      <div className="overflow-x-auto">
        <table className="w-full">
          <thead>
            <tr className="bg-gray-200">
              <th className="px-4 py-2">ID</th>
              <th className="px-4 py-2">Type</th>
              <th className="px-4 py-2">Brand</th>
              <th className="px-4 py-2">Model</th>
              <th className="px-4 py-2">Status</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {equipment.map(item => (
              <tr key={item.id_materiel} className="border-b">
                <td className="px-4 py-2">{item.id_materiel}</td>
                <td className="px-4 py-2">{item.type_materiel}</td>
                <td className="px-4 py-2">{item.marque}</td>
                <td className="px-4 py-2">{item.modele}</td>
                <td className="px-4 py-2">
                  <span className={`status-${item.etat.toLowerCase().replace(' ', '-')}`}>
                    {item.etat}
                  </span>
                </td>
                <td className="px-4 py-2 space-x-2">
                  <button 
                    onClick={() => navigate(`/equipment/edit/${item.id_materiel}`)}
                    className="btn-edit"
                  >
                    Edit
                  </button>
                  <button 
                    onClick={() => handleDelete(item.id_materiel)}
                    className="btn-delete"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Testimonials Section */}
      <section className="mt-12 bg-gray-100 p-6 rounded">
        <h2 className="text-xl font-bold mb-4">Equipment Management Benefits</h2>
        <p>"Our equipment tracking has improved by 80% since implementing this system."</p>
        <p className="font-semibold mt-2">- IT Manager</p>
      </section>
    </div>
  );
};

export default Equipment;
