import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const EditAttribution = () => {
    const { id_materiel } = useParams();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({});

    useEffect(() => {
        const assignments = JSON.parse(localStorage.getItem('attributions') || '[]');
        const selectedAssignment = assignments.find(a => a.id_materiel === id_materiel);
        if (selectedAssignment) {
            setFormData(selectedAssignment);
        }
    }, [id_materiel]);

    const handleChange = (e) => {
        setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        let assignments = JSON.parse(localStorage.getItem('attributions') || '[]');
        assignments = assignments.map(a => (a.id_materiel === id_materiel ? formData : a));
        localStorage.setItem('attributions', JSON.stringify(assignments));
        navigate('/Attributions');
    };

    return (
        <div>
            <h1>Edit Attribution</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Return Date:
                    <input type="date" name="date_retour" value={formData.date_retour || ''} onChange={handleChange} />
                </label>
                <button type="submit">Update</button>
            </form>
        </div>
    );
};

export default EditAttribution;
