import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Equipment = () => {
    const navigate = useNavigate();
    const [equipment, setEquipment] = useState([]);

    useEffect(() => {
        const storedEquipment = JSON.parse(localStorage.getItem('materiel') || '[]');
        setEquipment(storedEquipment);
    }, []);

    const handleDelete = (id) => {
        if (window.confirm("Are you sure you want to delete this equipment?")) {
            const updatedEquipment = equipment.filter(item => item.id_materiel !== id);
            setEquipment(updatedEquipment);
            localStorage.setItem('materiel', JSON.stringify(updatedEquipment));
        }
    };

    return (
        <div className="equipment-page">
            <section className="header-section">
                <h1>Equipment</h1>
                <button onClick={() => navigate('/AddEquipment')}>Add New Equipment</button>
            </section>

            <section className="equipment-list">
                <h2>Available Equipment</h2>
                {equipment.length === 0 ? (
                    <p>No equipment available.</p>
                ) : (
                    <table>
                        <thead>
                                <tr>
                                <th>ID</th>
                                <th>Type</th>
                                <th>Brand</th>
                                <th>Status</th>
                                {/* <th>Actions</th> */}
                            </tr>
                        </thead>
                        <tbody>
                            {equipment.map((item) => (
                                <tr key={item.id_materiel}>
                                    
                                    <td>{item.type_materiel || item.type}</td>
                                    <td>{item.marque || item.brand}</td>
                                    <td>{item.etat || item.status}</td>
                                    <td>
                                        <button onClick={() => navigate(`/EditEquipment/${item.id_materiel}`)}>Edit</button>
                                        <button onClick={() => handleDelete(item.id_materiel)} className="delete-btn">Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </section>
        </div>
    );
};

export default Equipment;
