import React from 'react';
import { useData } from '../../Context/DataContext';
import { Link, useNavigate } from 'react-router-dom';

const Employees = () => {
  const { employees, setEmployees } = useData();
  const navigate = useNavigate();

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this employee?")) {
      setEmployees(employees.filter(emp => emp.id_employee !== id));
    }
  };

  return (
    <div className="employees-page">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-2xl font-bold">Employees</h1>
        <button onClick={() => navigate('/employees/add')} className="add-employee-btn">
                Add New Employee
            </button>
      </div>

      <div className="overflow-x-auto">
        <table className="w-full">
          <thead>
            <tr className="bg-gray-200">
              <th className="px-4 py-2">ID</th>
              <th className="px-4 py-2">First Name</th>
              <th className="px-4 py-2">Last Name</th>
              <th className="px-4 py-2">Email</th>
              <th className="px-4 py-2">Position</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {employees.map(employee => (
              <tr key={employee.id_employee} className="border-b">
                <td className="px-4 py-2">{employee.id_employee}</td>
                <td className="px-4 py-2">{employee.prenom}</td>
                <td className="px-4 py-2">{employee.nom}</td>
                <td className="px-4 py-2">{employee.email}</td>
                <td className="px-4 py-2">{employee.poste}</td>
                <td className="px-4 py-2 space-x-2">
                  <button 
                    onClick={() => navigate(`/employees/edit/${employee.id_employee}`)}
                    className="btn-edit"
                  >
                    Edit
                  </button>
                  <button 
                    onClick={() => handleDelete(employee.id_employee)}
                    className="btn-delete"
                  >
                    Delete
                  </button>
                  <button 
                    onClick={() => navigate(`/attribution/assign?employeeId=${employee.id_employee}`)}
                    className="btn-secondary"
                  >
                    Assign Equipment
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* FAQ Section */}
      <section className="mt-12">
        <h2 className="text-xl font-bold mb-4">Employee Management FAQ</h2>
        <div className="space-y-4">
          <div>
            <h3 className="font-semibold">How to add a new employee?</h3>
            <p>Click on "Add New Employee" button and fill the form.</p>
          </div>
          {/* Add more FAQs as needed */}
        </div>
      </section>
    </div>
  );
};

export default Employees;
