import React, { useState, useEffect } from 'react';
import { useData } from '../../Context/DataContext';
import { useNavigate } from 'react-router-dom';

const AssignEquipment = () => {
  const { employees, equipment, attributions, setAttributions, setEquipment } = useData();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    id_employee: '',
    id_materiel: '',
    date_attribution: new Date().toISOString().split('T')[0],
    date_retour: ''
  });

  // Filter available equipment (status = "Disponible")
  const availableEquipment = equipment.filter(item => 
    item.etat === 'Disponible' || 
    item.id_materiel.toString() === formData.id_materiel
  );

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Validate selection
    if (!formData.id_employee || !formData.id_materiel) {
      alert('Please select both an employee and equipment');
      return;
    }

    // Create new assignment
    const newAssignment = {
      id_attribution: Date.now(),
      ...formData
    };

    // Update equipment status to "Attribué"
    const updatedEquipment = equipment.map(item => 
      item.id_materiel.toString() === formData.id_materiel 
        ? { ...item, etat: 'Attribué' } 
        : item
    );

    // Update state
    setAttributions([...attributions, newAssignment]);
    setEquipment(updatedEquipment);
    navigate('/attribution');   
    // Show success message and navigate back
    alert('Equipment assigned successfully!');

  };

  // Get employee name by ID
  const getEmployeeName = (id) => {
    const emp = employees.find(e => e.id_employee.toString() === id.toString());
    return emp ? `${emp.prenom} ${emp.nom}` : 'Unknown';
  };

  // Get equipment details by ID
  const getEquipmentDetails = (id) => {
    const eq = equipment.find(e => e.id_materiel.toString() === id.toString());
    return eq ? `${eq.type_materiel} (${eq.marque})` : 'Unknown';
  };

  return (
    <div className="assign-equipment-container">
      <h2 className="assign-header">Assign Equipment to Employee</h2>
      
      <form onSubmit={handleSubmit} className="assign-form">
        <div className="form-group">
          <label>Employee:</label>
          <select
            name="id_employee"
            value={formData.id_employee}
            onChange={handleChange}
            required
          >
            <option value="">Select Employee</option>
            {employees.map(emp => (
              <option key={emp.id_employee} value={emp.id_employee}>
                {emp.prenom} {emp.nom} ({emp.poste})
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Equipment:</label>
          <select
            name="id_materiel"
            value={formData.id_materiel}
            onChange={handleChange}
            required
          >
            <option value="">Select Equipment</option>
            {availableEquipment.map(item => (
              <option key={item.id_materiel} value={item.id_materiel}>
                {item.type_materiel} - {item.marque} ({item.etat})
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Assignment Date:</label>
          <input
            type="date"
            name="date_attribution"
            value={formData.date_attribution}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Return Date (optional):</label>
          <input
            type="date"
            name="date_retour"
            value={formData.date_retour}
            onChange={handleChange}
          />
        </div>

        <div className="button-group">
          <button type="submit" className="assign-action-btn">
            Confirm Assignment
          </button>
          <button 
            type="button" 
            onClick={() => navigate('/attribution')}
            className="btn-secondary"
          >
            Cancel
          </button>
        </div>
      </form>

      {/* Preview Section */}
      {formData.id_employee && formData.id_materiel && (
        <div className="assignment-preview">
          <h3>Assignment Preview</h3>
          <p><strong>Employee:</strong> {getEmployeeName(formData.id_employee)}</p>
          <p><strong>Equipment:</strong> {getEquipmentDetails(formData.id_materiel)}</p>
          <p><strong>Assignment Date:</strong> {formData.date_attribution}</p>
          {formData.date_retour && (
            <p><strong>Planned Return:</strong> {formData.date_retour}</p>
          )}
        </div>
      )}
    </div>
  );
};

export default AssignEquipment;
