import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AddEquipment = () => {
    const [formData, setFormData] = useState({
        id_materiel: '',
        type_materiel: '',
        marque: '',
        modele: '',
        etat: 'Disponible'
    });
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const equipment = JSON.parse(localStorage.getItem('materiel') || []);
        
        // Generate ID if not provided
        if (!formData.id_materiel) {
            formData.id_materiel = 'MAT-' + Date.now();
        }
        
        equipment.push(formData);
        localStorage.setItem('materiel', JSON.stringify(equipment));
        navigate('/Equipment');
    };

    return (
        <div className="add-equipment-page">
            <section className="header-section">
                <h1>Ajouter Matériel</h1>
            </section>
            
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="id_materiel"
                    placeholder="ID Matériel (auto-généré si vide)"
                    value={formData.id_materiel}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="type_materiel"
                    placeholder="Type de matériel"
                    value={formData.type_materiel}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="marque"
                    placeholder="Marque"
                    value={formData.marque}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="modele"
                    placeholder="Modèle"
                    value={formData.modele}
                    onChange={handleChange}
                    required
                />
                <select
                    name="etat"
                    value={formData.etat}
                    onChange={handleChange}
                >
                    <option value="Disponible">Disponible</option>
                    <option value="En panne">En panne</option>
                    <option value="En maintenance">En maintenance</option>
                </select>
                <button type="submit">Enregistrer</button>
            </form>
        </div>
    );
};

export default AddEquipment;
