import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AssignEquipment = () => {
    const [formData, setFormData] = useState({
        id_employee: '',
        id_materiel: '',
        date_attribution: new Date().toISOString().split('T')[0],
        date_retour: ''
    });

    const [employees, setEmployees] = useState([]);
    const [availableEquipment, setAvailableEquipment] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        setEmployees(JSON.parse(localStorage.getItem('employees') || '[]'));
        const equipment = JSON.parse(localStorage.getItem('materiel') || '[]');
        setAvailableEquipment(equipment.filter(item => item.etat === 'Disponible'));
    }, []);

    const handleChange = (e) => {
        setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        let assignments = JSON.parse(localStorage.getItem('attributions') || '[]');

        // Prevent duplicate assignment
        if (assignments.find(a => a.id_materiel === formData.id_materiel)) {
            alert("This equipment is already assigned.");
            return;
        }

        assignments.push({
            ...formData,
            employee_name: employees.find(e => e.id_employee == formData.id_employee)?.name,
            equipment_name: availableEquipment.find(e => e.id_materiel == formData.id_materiel)?.type_materiel
        });

        let updatedEquipment = JSON.parse(localStorage.getItem('materiel') || '[]').map(item =>
            item.id_materiel === formData.id_materiel ? { ...item, etat: 'Attribué' } : item
        );

        localStorage.setItem('materiel', JSON.stringify(updatedEquipment));
        localStorage.setItem('attributions', JSON.stringify(assignments));

        navigate('/Attributions');
    };

    return (
        <div className="assign-equipment-page">
            <h1>Assign Equipment</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Employee:
                    <select name="id_employee" value={formData.id_employee} onChange={handleChange} required>
                        <option value="">Select Employee</option>
                        {employees.map(emp => (
                            <option key={emp.id_employee} value={emp.id_employee}>{emp.name}</option>
                        ))}
                    </select>
                </label>

                <label>
                    Equipment:
                    <select name="id_materiel" value={formData.id_materiel} onChange={handleChange} required>
                        <option value="">Select Equipment</option>
                        {availableEquipment.map(item => (
                            <option key={item.id_materiel} value={item.id_materiel}>
                                {item.type_materiel} - {item.marque}
                            </option>
                        ))}
                    </select>
                </label>

                <button type="submit">Assign Equipment</button>
            </form>
        </div>
    );
};

export default AssignEquipment;
