import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const EmployeeAssignments = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [assignments, setAssignments] = useState([]);
    const [employee, setEmployee] = useState(null);

    useEffect(() => {
        const employees = JSON.parse(localStorage.getItem('employees') || '[]');
        const assignedEmployee = employees.find(emp => emp.id_employee === parseInt(id));
        setEmployee(assignedEmployee);

        const allAssignments = JSON.parse(localStorage.getItem('attributions') || '[]');
        setAssignments(allAssignments.filter(assign => assign.id_employee === id));
    }, [id]);

    return (
        <div className="assignments-page">
            <h1>{employee ? `Assignments for ${employee.prenom} ${employee.nom}` : 'Employee Not Found'}</h1>
            <button onClick={() => navigate('/Employees')}>Back to Employees</button>

            {assignments.length > 0 ? (
                <table>
                    <thead>
                        <tr>
                            <th>Equipment</th>
                            <th>Assignment Date</th>
                            <th>Return Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {assignments.map((assign, index) => (
                            <tr key={index}>
                                <td>{assign.equipment_name}</td>
                                <td>{assign.date_attribution}</td>
                                <td>{assign.date_retour || 'Not Returned'}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No assignments found for this employee.</p>
            )}
        </div>
    );
};

export default EmployeeAssignments;
