import React from 'react';
import { useData } from '../../Context/DataContext';
import { useNavigate } from 'react-router-dom';

const Employees = () => {
  const navigate = useNavigate();
  const { employees, setEmployees } = useData();

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this employee?")) {
      setEmployees(employees.filter(emp => emp.id_employee !== id));
    }
  };

  return (
    <div className="employees-page">
      <div className="page-header">
        <h1>Employees</h1>
        <button 
          onClick={() => navigate('/employees/add')} 
          className="btn btn-primary"
        >
          Add New Employee
        </button>
      </div>

      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Email</th>
              <th>Position</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((employee) => (
              <tr key={employee.id_employee}>
                <td>{employee.id_employee}</td>
                <td>{employee.prenom}</td>
                <td>{employee.nom}</td>
                <td>{employee.email}</td>
                <td>{employee.poste}</td>
                <td className="actions">
                  <button 
                    onClick={() => navigate(`/employees/edit/${employee.id_employee}`)}
                    className="btn btn-edit"
                  >
                    Edit
                  </button>
                  <button 
                    onClick={() => handleDelete(employee.id_employee)}
                    className="btn btn-danger"
                  >
                    Delete
                  </button>
                  <button 
                    onClick={() => navigate(`/attribution/assign?employeeId=${employee.id_employee}`)}
                    className="btn btn-assign"
                  >
                    Assign Equipment
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Employees;
