import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const AddEditEmployee = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        prenom: '',
        nom: '',
        email: '',
        position: ''
    });

    useEffect(() => {
        if (id) {
            const employees = JSON.parse(localStorage.getItem('employees') || '[]');
            const employeeToEdit = employees.find(emp => emp.id_employee === parseInt(id));
            if (employeeToEdit) {
                setFormData(employeeToEdit);
            }
        }
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        let employees = JSON.parse(localStorage.getItem('employees') || '[]');

        if (id) {
            // Edit existing employee
            employees = employees.map(emp => emp.id_employee === parseInt(id) ? formData : emp);
        } else {
            // Add new employee
            const newEmployee = {
                id_employee: employees.length > 0 
                    ? Math.max(...employees.map(emp => parseInt(emp.id_employee || 0))) + 1 
                    : 1,
                ...formData
            };
            employees.push(newEmployee);
        }

        localStorage.setItem('employees', JSON.stringify(employees));
        navigate('/Employees');
    };

    return (
        <div className="add-employee-page">
            <h1>{id ? 'Edit Employee' : 'Add Employee'}</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" name="prenom" placeholder="First Name" value={formData.prenom} onChange={handleChange} required />
                <input type="text" name="nom" placeholder="Last Name" value={formData.nom} onChange={handleChange} required />
                <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
                <input type="text" name="position" placeholder="Position" value={formData.position} onChange={handleChange} required />
                <button type="submit">{id ? 'Update Employee' : 'Add Employee'}</button>
            </form>
        </div>
    );
};

export default AddEditEmployee;
