import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AddEquipment = () => {
    const [formData, setFormData] = useState({
        id: '',
        name: '',
        type: '',
        status: 'Available'
    });
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Generate unique ID if not provided
        if (!formData.id) {
            formData.id = 'EQ-' + Math.random().toString(36).substr(2, 9);
        }
        
        // Store in localStorage
        const equipment = JSON.parse(localStorage.getItem('equipment') || '[]');
        equipment.push(formData);
        localStorage.setItem('equipment', JSON.stringify(equipment));
        
        // Redirect to Equipment page
        navigate('/Equipment');
    };

    return (
        <div className="add-equipment-page">
            <section className="header-section">
                <h1>Add Equipment</h1>
                <p>Add new equipment to your inventory</p>
            </section>
            
            <section className="application-form-section">
                <form onSubmit={handleSubmit}>
                    <input 
                        type="text" 
                        name="id" 
                        placeholder="Equipment ID (optional)" 
                        value={formData.id}
                        onChange={handleChange}
                    />
                    <input 
                        type="text" 
                        name="name" 
                        placeholder="Equipment Name" 
                        value={formData.name}
                        onChange={handleChange}
                        required 
                    />
                    <input 
                        type="text" 
                        name="type" 
                        placeholder="Equipment Type" 
                        value={formData.type}
                        onChange={handleChange}
                        required 
                    />
                    <select 
                        name="status" 
                        value={formData.status}
                        onChange={handleChange}
                    >
                        <option value="Available">Available</option>
                        <option value="Assigned">Assigned</option>
                        <option value="Maintenance">Maintenance</option>
                    </select>
                    <button type="submit">Add Equipment</button>
                </form>
            </section>
        </div>
    );
};

export default AddEquipment;
