import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Attributions = () => {
    const [assignments, setAssignments] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        setAssignments(JSON.parse(localStorage.getItem('attributions') || '[]'));
    }, []);

    const handleDelete = (id_materiel) => {
        let updatedAssignments = assignments.filter(a => a.id_materiel !== id_materiel);
        localStorage.setItem('attributions', JSON.stringify(updatedAssignments));
        
        let equipment = JSON.parse(localStorage.getItem('materiel') || '[]').map(item =>
            item.id_materiel === id_materiel ? { ...item, etat: 'Disponible' } : item
        );
        localStorage.setItem('materiel', JSON.stringify(equipment));

        setAssignments(updatedAssignments);
    };

    return (
        <div>
            <h1>Equipment Assignments</h1>
            <table>
                <thead>
                    <tr>
                        <th>Employee</th>
                        <th>Equipment</th>
                        <th>Date Assigned</th>
                        <th>Return Date</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {assignments.map((a, index) => (
                        <tr key={index}>
                            <td>{a.employee_name}</td>
                            <td>{a.equipment_name}</td>
                            <td>{a.date_attribution}</td>
                            <td>{a.date_retour || 'N/A'}</td>
                            <td>
                                <button onClick={() => navigate(`/EditAttribution/${a.id_materiel}`)}>Edit</button>
                                <button onClick={() => handleDelete(a.id_materiel)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Attributions;
