import React from 'react';
import { useNavigate } from 'react-router-dom';

const Employees = () => {
    const navigate = useNavigate();
    const [employees, setEmployees] = useState([]);

    useEffect(() => {
        const storedEmployees = JSON.parse(localStorage.getItem('employees') || '[]');
        setEmployees(storedEmployees);
    }, []);

    const handleDelete = (id) => {
        if (window.confirm("Are you sure you want to delete this employee?")) {
            const updatedEmployees = employees.filter(emp => emp.id_employee !== id);
            localStorage.setItem('employees', JSON.stringify(updatedEmployees));
            setEmployees(updatedEmployees);
        }
    };

    return (
        <div className="employees-page">
            <h1>Employees</h1>
            <button onClick={() => navigate('/AddEmployee')} className="add-employee-btn">
                Add New Employee
            </button>

            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Email</th>
                        <th>Position</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((emp) => (
                        <tr key={emp.id_employee}>
                            <td>{emp.id_employee}</td>
                            <td>{emp.prenom}</td>
                            <td>{emp.nom}</td>
                            <td>{emp.email}</td>
                            <td>{emp.position}</td>
                            <td>
                                <button onClick={() => navigate(`/EditEmployee/${emp.id_employee}`)}>Edit</button>
                                <button onClick={() => handleDelete(emp.id_employee)}>Delete</button>
                                <button onClick={() => navigate(`/EmployeeAssignments/${emp.id_employee}`)}>View Assignments</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Employees;
