import React, { useState, useEffect } from 'react';
import { useData } from '../../Context/DataContext';
import { useNavigate, useParams } from 'react-router-dom';

const EditEquipment = () => {
  const { equipment, setEquipment } = useData();
  const { id } = useParams();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    type_materiel: '',
    marque: '',
    modele: '',
    etat: ''
  });

  useEffect(() => {
    const item = equipment.find(eq => eq.id_materiel.toString() === id);
    if (item) {
      setForm({
        type_materiel: item.type_materiel,
        marque: item.marque,
        modele: item.modele,
        etat: item.etat
      });
    } else {
      navigate('/equipment');
    }
  }, [id, equipment]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const updatedEquipment = equipment.map(eq => 
      eq.id_materiel.toString() === id ? { ...eq, ...form } : eq
    );
    setEquipment(updatedEquipment);
    navigate('/equipment');
  };

  return (
    <div className="edit-equipment-page">
      <h1 className="text-2xl font-bold mb-6">Edit Equipment</h1>
      
      <form onSubmit={handleSubmit} className="max-w-md space-y-4">
        <div>
          <label className="block mb-1">Equipment Type</label>
          <input
            type="text"
            value={form.type_materiel}
            onChange={(e) => setForm({...form, type_materiel: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Brand</label>
          <input
            type="text"
            value={form.marque}
            onChange={(e) => setForm({...form, marque: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Model</label>
          <input
            type="text"
            value={form.modele}
            onChange={(e) => setForm({...form, modele: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Status</label>
          <select
            value={form.etat}
            onChange={(e) => setForm({...form, etat: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          >
            <option value="Disponible">Available</option>
            <option value="Attribué">Assigned</option>
            <option value="En réparation">In Repair</option>
            <option value="Hors service">Out of Service</option>
          </select>
        </div>

        <div className="flex space-x-4 pt-4">
          <button type="submit" className="btn-primary">
            Save Changes
          </button>
          <button 
            type="button" 
            onClick={() => navigate('/equipment')}
            className="btn-secondary"
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditEquipment;
