import React from 'react';
import { useData } from '../../Context/DataContext';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faCheck, faTimes } from '@fortawesome/free-solid-svg-icons';

const Attribution = () => {
  const { attributions, employees, equipment, returnEquipment } = useData();
  const navigate = useNavigate();

  // Group attributions by employee
  const groupedAttributions = attributions.reduce((acc, attribution) => {
    const employeeId = attribution.id_employee;
    if (!acc[employeeId]) {
      acc[employeeId] = {
        employee: employees.find(e => e.id_employee === employeeId),
        assignments: []
      };
    }
    
    const eq = equipment.find(e => e.id_materiel === attribution.id_materiel);
    acc[employeeId].assignments.push({
      equipment: eq,
      attribution,
      isReturned: !!attribution.date_retour
    });
    
    return acc;
  }, {});

  const handleReturn = async (attributionId) => {
    if (window.confirm("Return this equipment?")) {
      try {
        await returnEquipment(attributionId);
      } catch (error) {
        console.error("Return error:", error);
      }
    }
  };

  const handleAssignMore = (employeeId) => {
    navigate(`/attribution/assign?employeeId=${employeeId}`);
  };

  return (
    <div className="attribution-page">
      <div className="attribution-header">
        <h1>Equipment Assignments</h1>
        <button 
          onClick={() => navigate('/attribution/assign')}
          className="btn-primary"
        >
          <FontAwesomeIcon icon={faPlus} /> New Assignment
        </button>
      </div>

      {/* Simple two-column table as shown in your image */}
      <div className="simple-table-wrapper">
        <table className="simple-table">
          <thead>
            <tr>
              <th>Attribute</th>
              <th>Return</th>
            </tr>
          </thead>
          <tbody>
            {Object.values(groupedAttributions).map((group, index) => (
              <React.Fragment key={index}>
                {group.assignments.map((assignment, idx) => (
                  <tr key={`${index}-${idx}`}>
                    <td>
                      <strong>{group.employee?.prenom} {group.employee?.nom}</strong>
                      <div>{assignment.equipment?.type_materiel} ({assignment.equipment?.marque})</div>
                      <div>Assigned: {new Date(assignment.attribution.date_attribution).toLocaleDateString()}</div>
                    </td>
                    <td>
                      {assignment.isReturned ? (
                        <span className="status-badge returned">
                          <FontAwesomeIcon icon={faCheck} /> Returned
                        </span>
                      ) : (
                        <button 
                          onClick={() => handleReturn(assignment.attribution.id_attribution)}
                          className="btn-return"
                        >
                          Return
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Attribution;

// Add this CSS to your styles
const styles = `
  .simple-table-wrapper {
    margin-top: 20px;
  }

  .simple-table {
    width: 100%;
    border-collapse: collapse;
    font-family: Arial, sans-serif;
  }

  .simple-table th {
    background-color: #f2f2f2;
    text-align: left;
    padding: 12px 15px;
    border-bottom: 1px solid #ddd;
    font-weight: 600;
  }

  .simple-table td {
    padding: 12px 15px;
    border-bottom: 1px solid #ddd;
    vertical-align: top;
  }

  .simple-table tr:last-child td {
    border-bottom: none;
  }

  .btn-return {
    background: #ffebee;
    color: #c62828;
    border: none;
    padding: 8px 12px;
    border-radius: 4px;
    cursor: pointer;
    font-size: 0.9em;
  }

  .btn-return:hover {
    background: #ffcdd2;
  }

  .status-badge {
    display: inline-flex;
    align-items: center;
    gap: 5px;
    padding: 6px 10px;
    border-radius: 12px;
    font-size: 0.85em;
  }

  .status-badge.returned {
    background: #e8f5e9;
    color: #2e7d32;
  }
`;

// Add styles to the head
const styleElement = document.createElement('style');
styleElement.innerHTML = styles;
document.head.appendChild(styleElement);
