import React, { useState } from 'react';
import { useData } from '../../Context/DataContext';  // Assuming you're using context to store equipment
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AddEquipment = () => {
  const { equipment, setEquipment } = useData();
  const navigate = useNavigate();

  // Initialize the form state
  const [form, setForm] = useState({
    type_materiel: '',
    marque: '',
    modele: '',
    etat: 'Disponible' // Default state
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newEquipment = {
      ...form,
      id_materiel: equipment.length > 0 ? Math.max(...equipment.map(e => e.id_materiel)) + 1 : 1
    };

    console.log('Sending new equipment:', newEquipment);  // Log the new equipment data for debugging

    try {
      // Send POST request to the backend to add the new equipment
      const response = await axios.post('http://localhost:5000/api/equipment', newEquipment);

      console.log('Equipment added successfully:', response.data);  // Log response data for debugging

      // Add the new equipment to the local state
      setEquipment(prevEquipment => [...prevEquipment, response.data]);

      // Redirect to the index page
      navigate('/equipment');
    } catch (error) {
      console.error("Error adding equipment:", error.response || error.message);
    }
  };

  return (
    <div className="add-equipment-page">
      <h1 className="text-2xl font-bold mb-6">Add New Equipment</h1>

      <form onSubmit={handleSubmit} className="max-w-md space-y-4">
        <div>
          <label className="block mb-1">Equipment Type</label>
          <input
            type="text"
            value={form.type_materiel}
            onChange={(e) => setForm({ ...form, type_materiel: e.target.value })}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Brand</label>
          <input
            type="text"
            value={form.marque}
            onChange={(e) => setForm({ ...form, marque: e.target.value })}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Model</label>
          <input
            type="text"
            value={form.modele}
            onChange={(e) => setForm({ ...form, modele: e.target.value })}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Status</label>
          <select
            value={form.etat}
            onChange={(e) => setForm({ ...form, etat: e.target.value })}
            className="w-full px-3 py-2 border rounded"
            required
          >
            <option value="Disponible">Available</option>
            <option value="Attribué">Assigned</option>
            <option value="En réparation">In Repair</option>
            <option value="Hors service">Out of Service</option>
          </select>
        </div>

        <div className="flex space-x-4 pt-4">
          <button type="submit" className="btn-primary">
            Add Equipment
          </button>
          <button
            type="button"
            onClick={() => navigate('/equipment')} // Navigate to the index page
            className="btn-secondary"
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddEquipment;
