import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const AddEditEmployee = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        prenom: '',
        nom: '',
        email: '',
        position: ''
    });
    const [loading, setLoading] = useState(false);

    // Fetch data for editing if an employee ID is passed in the URL
    useEffect(() => {
        if (id) {
            setLoading(true);
            const employees = JSON.parse(localStorage.getItem('employees') || '[]');
            const employeeToEdit = employees.find(emp => emp.id_employee === parseInt(id));
            if (employeeToEdit) {
                setFormData(employeeToEdit);
            }
            setLoading(false);
        }
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        
        // Simple email validation
        if (!/\S+@\S+\.\S+/.test(formData.email)) {
            alert("Please enter a valid email address.");
            return;
        }

        let employees = JSON.parse(localStorage.getItem('employees') || '[]');

        if (id) {
            // Edit existing employee
            employees = employees.map(emp => emp.id_employee === parseInt(id) ? formData : emp);
        } else {
            // Add new employee
            const newEmployee = {
                id_employee: employees.length > 0 
                    ? Math.max(...employees.map(emp => parseInt(emp.id_employee || 0))) + 1 
                    : 1,
                ...formData
            };
            employees.push(newEmployee);
        }

        // Save updated employee list back to localStorage
        localStorage.setItem('employees', JSON.stringify(employees));

        // Notify user of success and navigate
        alert(id ? 'Employee updated successfully!' : 'Employee added successfully!');
        navigate('/Employees');
    };

    // Show loading state while fetching data
    if (loading) return <div>Loading...</div>;

    return (
        <div className="add-employee-page">
            <h1>{id ? 'Edit Employee' : 'Add Employee'}</h1>
            <form onSubmit={handleSubmit}>
                <input 
                    type="text" 
                    name="prenom" 
                    placeholder="First Name" 
                    value={formData.prenom} 
                    onChange={handleChange} 
                    required 
                />
                <input 
                    type="text" 
                    name="nom" 
                    placeholder="Last Name" 
                    value={formData.nom} 
                    onChange={handleChange} 
                    required 
                />
                <input 
                    type="email" 
                    name="email" 
                    placeholder="Email" 
                    value={formData.email} 
                    onChange={handleChange} 
                    required 
                />
                <input 
                    type="text" 
                    name="position" 
                    placeholder="Position" 
                    value={formData.position} 
                    onChange={handleChange} 
                    required 
                />
                <button type="submit">{id ? 'Update Employee' : 'Add Employee'}</button>
            </form>
        </div>
    );
};

export default AddEditEmployee;
