import React, { useState } from 'react';
import { useData } from '../../Context/DataContext';
import { useNavigate } from 'react-router-dom';

const AssignEquipment = () => {
  const { employees, equipment, assignEquipment } = useData();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    id_employee: '',
    id_materiel: ''
  });

  const availableEquipment = equipment.filter(
    item => item.etat === 'Disponible' || 
           item.id_materiel.toString() === formData.id_materiel
  );

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.id_employee || !formData.id_materiel) {
      alert('Please select both an employee and equipment');
      return;
    }

    try {
      await assignEquipment(
        parseInt(formData.id_employee),
        parseInt(formData.id_materiel)
      );
      navigate('/attribution');
    } catch (error) {
      alert('Failed to assign equipment');
    }
  };

  const getEmployeeName = (id) => {
    const emp = employees.find(e => e.id_employee.toString() === id.toString());
    return emp ? `${emp.prenom} ${emp.nom}` : 'Unknown';
  };

  const getEquipmentDetails = (id) => {
    const eq = equipment.find(e => e.id_materiel.toString() === id.toString());
    return eq ? `${eq.type_materiel} (${eq.marque})` : 'Unknown';
  };

  return (
    <div className="assign-equipment-container">
      <h2>Assign Equipment to Employee</h2>
      
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Employee:</label>
          <select
            name="id_employee"
            value={formData.id_employee}
            onChange={(e) => setFormData({...formData, id_employee: e.target.value})}
            required
          >
            <option value="">Select Employee</option>
            {employees.map(emp => (
              <option key={emp.id_employee} value={emp.id_employee}>
                {emp.prenom} {emp.nom} ({emp.poste})
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Equipment:</label>
          <select
            name="id_materiel"
            value={formData.id_materiel}
            onChange={(e) => setFormData({...formData, id_materiel: e.target.value})}
            required
          >
            <option value="">Select Equipment</option>
            {availableEquipment.map(item => (
              <option key={item.id_materiel} value={item.id_materiel}>
                {item.type_materiel} - {item.marque} ({item.etat})
              </option>
            ))}
          </select>
        </div>

        <div className="button-group">
          <button type="submit">Confirm Assignment</button>
          <button type="button" onClick={() => navigate('/attribution')}>
            Cancel
          </button>
        </div>
      </form>

      {formData.id_employee && formData.id_materiel && (
        <div className="assignment-preview">
          <h3>Assignment Preview</h3>
          <p><strong>Employee:</strong> {getEmployeeName(formData.id_employee)}</p>
          <p><strong>Equipment:</strong> {getEquipmentDetails(formData.id_materiel)}</p>
        </div>
      )}
    </div>
  );
};

export default AssignEquipment;
