import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UAttribution = () => {
  const [groupedAttributions, setGroupedAttributions] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        const [attrRes, empRes, eqRes] = await Promise.all([
          axios.get('http://localhost:5000/api/attributions'),
          axios.get('http://localhost:5000/api/employees'),
          axios.get('http://localhost:5000/api/equipment')
        ]);

        const employeesMap = new Map(empRes.data.map(e => [e.id_employee, e]));
        const equipmentMap = new Map(eqRes.data.map(eq => [eq.id_materiel, eq]));

        // Fusion et regroupement par employ√©
        const grouped = {};

        attrRes.data.forEach(attr => {
          const employee = employeesMap.get(attr.id_employee);
          const equipment = equipmentMap.get(attr.id_materiel);
          const key = attr.id_employee;

          if (!grouped[key]) {
            grouped[key] = {
              employee,
              attributions: []
            };
          }

          grouped[key].attributions.push({
            ...attr,
            equipment
          });
        });

        setGroupedAttributions(grouped);
        setError(null);
      } catch (err) {
        setError("Erreur de chargement");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchAllData();
  }, []);

  if (loading) return <div>Chargement...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Attributions par Employ√©</h1>

      {Object.keys(groupedAttributions).length === 0 ? (
        <p>Aucune attribution trouv√©e.</p>
      ) : (
        Object.values(groupedAttributions).map(group => (
          <div key={group.employee.id_employee} className="mb-6 border-b pb-4">
            <h2 className="text-lg font-semibold">
              üë§ {group.employee.prenom} {group.employee.nom}
            </h2>
            <ul className="pl-4 mt-2 list-disc text-sm">
              {group.attributions.map(attr => (
                <li key={attr.id_attribution}>
                  {attr.equipment?.type_materiel} ‚Äî attribu√© le {new Date(attr.date_attribution).toLocaleDateString()} ‚Äî {attr.date_retour ? 'Retourn√©' : 'Attribu√©'}
                </li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default UAttribution;
