import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faCheck, faTimes, faTrash } from '@fortawesome/free-solid-svg-icons';

const Attribution = () => {
  const navigate = useNavigate();
  const [attributions, setAttributions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAttributions = async () => {
      try {
        // Fetch attribution data
        const response = await axios.get('http://localhost:5000/api/attributions');
        const attributionData = response.data;

        // Fetch employee and equipment data
        const employeesResponse = await axios.get('http://localhost:5000/api/employees');
        const equipmentResponse = await axios.get('http://localhost:5000/api/equipment');

        const attributionsWithDetails = attributionData.map(attribution => {
          const employee = employeesResponse.data.find(e => e.id_employee === attribution.id_employee);
          const equip = equipmentResponse.data.find(eq => eq.id_materiel === attribution.id_materiel);

          return {
            ...attribution,
            employee,
            equipment: equip
          };
        });

        setAttributions(attributionsWithDetails);
        setError(null);
      } catch (err) {
        setError(err.message);
        console.error("Error fetching attributions:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchAttributions();
  }, []); // Empty dependency array to only fetch data on mount

  const handleReturn = async (attributionId) => {
    try {
      const response = await axios.put(`http://localhost:5000/api/attributions/${attributionId}/return`);
      const updatedAttribution = response.data;

      setAttributions((prevAttributions) =>
        prevAttributions.map((attribution) =>
          attribution.id_attribution === attributionId ? updatedAttribution : attribution
        )
      );
    } catch (err) {
      setError('Failed to update return status. Please try again later.');
      console.error('Error returning equipment:', err);
    }
  };

  const handleDelete = async (attributionId) => {
    try {
      await axios.delete(`http://localhost:5000/api/attributions/${attributionId}`);
      setAttributions((prevAttributions) =>
        prevAttributions.filter((attribution) => attribution.id_attribution !== attributionId)
      );
    } catch (err) {
      setError('Failed to delete attribution. Please try again later.');
      console.error('Error deleting attribution:', err);
    }
  };

  if (loading) return <div className="loading">Loading attributions...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="attribution-container">
      <div className="attribution-header">
        <h1>Equipment Assignments</h1>
        <button 
          onClick={() => navigate('/attribution/assign')}
          className="btn-primary"
        >
          <FontAwesomeIcon icon={faPlus} /> New Assignment
        </button>
      </div>

      {attributions.length === 0 ? (
        <div className="empty-state">
          <p>No attributions found.</p>
        </div>
      ) : (
        <table className="attribution-table">
          <thead>
            <tr>
              <th>Employee</th>
              <th>Equipment</th>
              <th>Assigned On</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {attributions.map((attribution) => (
              <tr key={attribution.id_attribution}>
                <td>{attribution.employee?.prenom} {attribution.employee?.nom}</td>
                <td>{attribution.equipment?.type_materiel}</td>
                <td>{new Date(attribution.date_attribution).toLocaleDateString()}</td>
                <td>{attribution.date_retour ? 'Returned' : 'Assigned'}</td>
                <td>
                  {attribution.date_retour ? (
                    <button disabled>Returned</button>
                  ) : (
                    <button onClick={() => handleReturn(attribution.id_attribution)}>Return</button>
                  )}
                  <button onClick={() => handleDelete(attribution.id_attribution)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Attribution;
