import React from 'react';
import { useNavigate } from 'react-router-dom';

const Employees = () => {
    const navigate = useNavigate();
    const [employees, setEmployees] = useState([]);

    useEffect(() => {
        const storedEmployees = JSON.parse(localStorage.getItem('employees') || '[]');
        setEmployees(storedEmployees);
    }, []);

    const handleDelete = (id) => {
        if (window.confirm("Are you sure you want to delete this employee?")) {
            const updatedEmployees = employees.filter(emp => emp.id_employee !== id);
            localStorage.setItem('employees', JSON.stringify(updatedEmployees));
            setEmployees(updatedEmployees);
        }
    };

    return (
        <div className="employees-page">
            <section className="header-section">
                <h1>Employees</h1>
                <p>Overview of employee management.</p>
                <button 
                    onClick={() => navigate('/AddEmployee')}
                    className="add-employee-btn"
                >
                    Add New Employee
                </button>
            </section>
            
            <section className="features-list-section">
                <h2>Features</h2>
                <ul>
                    <li>Adding employees</li>
                    <li>Editing employees</li>
                    <li>Removing employees</li>
                </ul>
            </section>
            
            <section className="team-section">
                <h2>Current Employees</h2>
                {/* Employee list would be rendered here */}
            </section>
            
            <section className="faq-section">
                <h2>FAQ</h2>
                {/* FAQ items would go here */}
            </section>
        </div>
    );
};

export default Employees;
