import React from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar';  // Import Navbar
import Home from './pages/home';  // Import Home
import Login from './pages/login';
import Signup from './pages/signup';
import Footer from './components/Footer';
import Attribution from './pages/Attribution';
import Employees from './pages/Employees';
import Equipment from './pages/Equipment';
import AddEmployee from './pages/AddEmployees';
import AddEquipment from './pages/AddEquipment';
import AssignEquipment from './pages/Attribution';
import { useEffect } from 'react';




const App = () => (
  <Router>  {/* Ensure the Router is wrapping everything */}
    <Navbar />
    <Routes>
      <Route path="/" element={<Home />} />
      <Route path="/login" element={<Login />} />
      <Route path="/signup" element={<Signup />} />
      <Route path="/Employees" element={<Employees />} />
      <Route path="/Equipment" element={<Equipment />} />
      <Route path="/Attribution" element={<Attribution />} />
      <Route path="/AddEmployee" element={<AddEmployee />} />
      <Route path="/AddEquipment" element={<AddEquipment />} />
      <Route path="/AssignEquipment" element={<AssignEquipment />} />
    </Routes>

    <Footer />
  </Router>

);

if (!localStorage.getItem('employes')) {
    localStorage.setItem('employes', JSON.stringify([]));
}
if (!localStorage.getItem('materiel')) {
    localStorage.setItem('materiel', JSON.stringify([]));
}
if (!localStorage.getItem('attributions')) {
    localStorage.setItem('attributions', JSON.stringify([]));
}
useEffect(() => {
    if (!localStorage.getItem('employes') && !localStorage.getItem('employees')) {
        localStorage.setItem('employes', JSON.stringify([
            {
                id_employee: 1,
                nom: "Doe",
                prenom: "John",
                email: "john@example.com",
                poste: "Manager"
            }
        ]));
    }
    if (!localStorage.getItem('materiel') && !localStorage.getItem('equipment')) {
        localStorage.setItem('materiel', JSON.stringify([
            {
                id_materiel: "EQ-1001",
                type_materiel: "Laptop",
                marque: "Dell",
                modele: "XPS 15",
                etat: "Disponible"
            }
        ]));
    }
}, []);

export default App;
