import React, { useState } from 'react';
import { useData } from '../../Context/DataContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AddEmployee = () => {
  const { employees, setEmployees } = useData();
  const navigate = useNavigate();
  
  // Initialize the form state
  const [form, setForm] = useState({
    nom: '',
    prenom: '',
    email: '',
    poste: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newEmployee = {
      ...form,
      id_employee: employees.length > 0 ? Math.max(...employees.map(e => e.id_employee)) + 1 : 1
    };

    try {
      // Send POST request to the backend to add the new employee
      const response = await axios.post('/api/employees', newEmployee);

      // Add the new employee to the local state
      setEmployees(prevEmployees => [...prevEmployees, response.data]);

      // Redirect to index.jsx (assuming it's the root page)
      navigate('/');
    } catch (error) {
      console.error("Error adding employee:", error);
    }
  };

  return (
    <div className="add-employee-page">
      <h1 className="text-2xl font-bold mb-6">Add New Employee</h1>
      
      <form onSubmit={handleSubmit} className="max-w-md space-y-4">
        <div>
          <label className="block mb-1">First Name</label>
          <input
            type="text"
            value={form.prenom}
            onChange={(e) => setForm({ ...form, prenom: e.target.value })}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Last Name</label>
          <input
            type="text"
            value={form.nom}
            onChange={(e) => setForm({ ...form, nom: e.target.value })}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Email</label>
          <input
            type="email"
            value={form.email}
            onChange={(e) => setForm({ ...form, email: e.target.value })}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Position</label>
          <input
            type="text"
            value={form.poste}
            onChange={(e) => setForm({ ...form, poste: e.target.value })}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div className="flex space-x-4 pt-4">
          <button type="submit" className="btn-primary">
            Add Employee
          </button>
          <button 
            type="button" 
            onClick={() => navigate('/')} // Navigate to index page
            className="btn-secondary"
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddEmployee;
