import React, { useState, useEffect } from 'react';
import { useData } from '../../Context/DataContext';  // Use the context
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

const EditEmployee = () => {
  const { employees, setEmployees } = useData(); // Use context to get employees
  const { id } = useParams(); // Get employee ID from URL
  const navigate = useNavigate();

  const [form, setForm] = useState({
    nom: '',
    prenom: '',
    email: '',
    poste: ''
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Check if employee is in the local context state
    const employee = employees.find(emp => emp.id_employee.toString() === id);

    if (employee) {
      setForm({
        nom: employee.nom,
        prenom: employee.prenom,
        email: employee.email,
        poste: employee.poste
      });
      setLoading(false); // Stop loading when data is found
    } else {
      // If employee is not found in context, fetch from the server (fallback)
const fetchEmployee = async () => {
  try {
    const response = await axios.get(`http://localhost:5000/api/employees/${id}`);
    const employeeData = response.data;
    if (employeeData) {
      setForm({
        nom: employeeData.nom,
        prenom: employeeData.prenom,
        email: employeeData.email,
        poste: employeeData.poste
      });
      setLoading(false);
    } else {
      navigate('/employees');
    }
  } catch (error) {
    console.error('Error fetching employee:', error);
    setError('Failed to fetch employee');
    setLoading(false);
    navigate('/employees');
  }
};

      fetchEmployee();
    }
  }, [id, employees, navigate]);

const handleSubmit = async (e) => {
  e.preventDefault();

  const updatedEmployee = {
    ...form,
    id_employee: parseInt(id) // Ensure the id is an integer
  };

  try {
    // Send PUT request to update the employee
    const response = await axios.put(`http://localhost:5000/api/employees/${id}`, updatedEmployee);

    // Check the response data
    if (response.status === 200) {
      // Update the local state with the new employee data
      setEmployees(prevEmployees =>
        prevEmployees.map(emp =>
          emp.id_employee === parseInt(id) ? response.data : emp
        )
      );
      // Redirect back to the employees list page
      navigate('/employees');
    } else {
      console.error('Failed to update employee:', response.data);
    }
  } catch (error) {
    console.error('Error updating employee:', error);
  }
};


  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="edit-employee-page">
      <h1 className="text-2xl font-bold mb-6">Edit Employee</h1>
      
      <form onSubmit={handleSubmit} className="max-w-md space-y-4">
        <div>
          <label className="block mb-1">First Name</label>
          <input
            type="text"
            value={form.prenom}
            onChange={(e) => setForm({...form, prenom: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Last Name</label>
          <input
            type="text"
            value={form.nom}
            onChange={(e) => setForm({...form, nom: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Email</label>
          <input
            type="email"
            value={form.email}
            onChange={(e) => setForm({...form, email: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Position</label>
          <input
            type="text"
            value={form.poste}
            onChange={(e) => setForm({...form, poste: e.target.value})}
            className="w-full px-3 py-2 border rounded"
            required
          />
        </div>

        <div className="flex space-x-4 pt-4">
          <button type="submit" className="btn-primary">
            Save Changes
          </button>
          <button 
            type="button" 
            onClick={() => navigate('/employees')}
            className="btn-secondary"
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditEmployee;
