import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const EditEquipment = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        id_materiel: '',
        type_materiel: '',
        marque: '',
        etat: ''
    });

    useEffect(() => {
        const storedEquipment = JSON.parse(localStorage.getItem('materiel') || '[]');
        const equipmentToEdit = storedEquipment.find(item => item.id_materiel === id);
        if (equipmentToEdit) {
            setFormData(equipmentToEdit);
        }
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const storedEquipment = JSON.parse(localStorage.getItem('materiel') || '[]');
        const updatedEquipment = storedEquipment.map(item => 
            item.id_materiel === id ? formData : item
        );

        localStorage.setItem('materiel', JSON.stringify(updatedEquipment));
        navigate('/Equipment');
    };

    return (
        <div className="edit-equipment-page">
            <h1>Edit Equipment</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Type:
                    <input type="text" name="type_materiel" value={formData.type_materiel} onChange={handleChange} required />
                </label>

                <label>
                    Brand:
                    <input type="text" name="marque" value={formData.marque} onChange={handleChange} required />
                </label>

                <label>
                    Status:
                    <select name="etat" value={formData.etat} onChange={handleChange} required>
                        <option value="Disponible">Disponible</option>
                        <option value="Attribué">Attribué</option>
                        <option value="Réparé">Réparé</option>
                        <option value="Hors service">Hors service</option>
                    </select>
                </label>

                <button type="submit">Save Changes</button>
            </form>
        </div>
    );
};

export default EditEquipment;
