import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faEdit, faTrash, faLaptop } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import '../../App.css';

const Equipment = () => {
  const navigate = useNavigate();
  const [equipment, setEquipment] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEquipment = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/equipment');
        setEquipment(response.data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchEquipment();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this equipment?")) {
      try {
        await axios.delete(`http://localhost:5000/api/equipment/${id}`);
        setEquipment(equipment.filter(item => item.id_materiel !== id));
      } catch (error) {
        console.error("Error deleting equipment:", error);
      }
    }
  };

  if (loading) return <div>Loading equipment...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="equipment-container">
      {/* ... keep your existing JSX, just update the status display ... */}
