        <span className={`status-badge ${
          form.etat === 'Disponible' ? 'status-available' :
          form.etat === 'Attribué' ? 'status-assigned' :
          form.etat === 'En réparation' ? 'status-repair' :
          'status-out'
        }`}>
          {form.etat}
        </span>
      </td>
      {/* ... rest of your JSX ... */}
    </div>
  );
};

export default Equipment;
