import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Equipment = () => {
    const navigate = useNavigate();
    const [equipment, setEquipment] = useState([]);

    useEffect(() => {
        // Load equipment from localStorage
        const storedEquipment = JSON.parse(localStorage.getItem('equipment') || '[]');
        setEquipment(storedEquipment);
    }, []);

    return (
        <div className="equipment-page">
            <section className="header-section">
                <h1>Equipment</h1>
                <p>Manage and view all equipment in your inventory.</p>
                <button 
                    onClick={() => navigate('/AddEquipment')}
                    className="add-equipment-btn"
                >
                    Add New Equipment
                </button>
            </section>
            
            <section className="features-list-section">
                <h2>Equipment Management</h2>
                <ul>
                    <li>Adding equipment</li>
                    <li>Editing equipment</li>
                    <li>Deleting equipment</li>
                </ul>
            </section>
            
            <section className="inventory-section">
                <h2>Current Inventory</h2>
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Type</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {equipment.map((item, index) => (
                            <tr key={index}>
                                <td>{item.id}</td>
                                <td>{item.name}</td>
                                <td>{item.type}</td>
                                <td>{item.status || 'Available'}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </section>
        </div>
    );
};

export default Equipment
