import React, { useEffect } from 'react';
import { useData } from '../../Context/DataContext';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';

const Attribution = () => {
  const { 
    attributions, 
    employees, 
    equipment, 
    returnEquipment 
  } = useData();
  const navigate = useNavigate();

  const handleReturn = async (id) => {
    if (window.confirm("Are you sure you want to mark this equipment as returned?")) {
      try {
        await returnEquipment(id);
      } catch (error) {
        console.error("Error returning equipment:", error);
      }
    }
  };

  const getEmployeeName = (id) => {
    const emp = employees.find(e => e.id_employee.toString() === id.toString());
    return emp ? `${emp.prenom} ${emp.nom}` : 'Unknown';
  };

  const getEquipmentDetails = (id) => {
    const eq = equipment.find(e => e.id_materiel.toString() === id.toString());
    return eq ? `${eq.type_materiel} (${eq.marque})` : 'Unknown';
  };

  return (
    <div className="attribution-page">
      <div className="attribution-header">
        <h1>Equipment Assignments</h1>
        <button onClick={() => navigate('/attribution/assign')}>
          <FontAwesomeIcon icon={faPlus} /> New Assignment
        </button>
      </div>

      <div className="attribution-table-wrapper">
        <table className="attribution-table">
          <thead>
            <tr>
              <th>Employee</th>
              <th>Equipment</th>
              <th>Assignment Date</th>
              <th>Return Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {attributions.map(attr => (
              <tr key={attr.id_attribution}>
                <td>{getEmployeeName(attr.id_employee)}</td>
                <td>{getEquipmentDetails(attr.id_materiel)}</td>
                <td>{new Date(attr.date_attribution).toLocaleDateString()}</td>
                <td>{attr.date_retour ? new Date(attr.date_retour).toLocaleDateString() : 'Not returned'}</td>
                <td>
                  {!attr.date_retour && (
                    <button onClick={() => handleReturn(attr.id_attribution)}>
                      Mark Returned
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Attribution;
