import React, { useEffect, useState } from 'react';

const Attributions = () => {
    const [assignments, setAssignments] = useState([]);
    const [employees, setEmployees] = useState([]);
    const [availableEquipment, setAvailableEquipment] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [formData, setFormData] = useState({
        id_employee: '',
        id_materiel: '',
        date_attribution: new Date().toISOString().split('T')[0],
        date_retour: ''
    });

    useEffect(() => {
        setAssignments(JSON.parse(localStorage.getItem('attributions') || '[]'));
        setEmployees(JSON.parse(localStorage.getItem('employees') || '[]'));
        
        const equipment = JSON.parse(localStorage.getItem('materiel') || '[]');
        setAvailableEquipment(equipment.filter(item => item.etat === 'Disponible'));
    }, []);

    const handleChange = (e) => {
        setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        let updatedAssignments = [...assignments];

        // Prevent duplicate assignment
        if (updatedAssignments.find(a => a.id_materiel === formData.id_materiel)) {
            alert("This equipment is already assigned.");
            return;
        }

        const newAssignment = {
            ...formData,
            employee_name: employees.find(e => e.id_employee == formData.id_employee)?.name,
            equipment_name: availableEquipment.find(e => e.id_materiel == formData.id_materiel)?.type_materiel
        };
        const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData); // Log formData to ensure the correct employee ID is selected
    
    const newAssignment = {
        ...formData,
        employee_name: employees.find(e => e.id_employee == formData.id_employee)
            ? `${employees.find(e => e.id_employee == formData.id_employee)?.prenom} ${employees.find(e => e.id_employee == formData.id_employee)?.nom}`
            : 'Unknown',
        equipment_name: availableEquipment.find(e => e.id_materiel == formData.id_materiel)?.type_materiel
    };

    console.log(newAssignment);  // Log the newAssignment to see if the employee name is correct

    // Update assignments...
};

        updatedAssignments.push(newAssignment);
        setAssignments(updatedAssignments);
        localStorage.setItem('attributions', JSON.stringify(updatedAssignments));

        let updatedEquipment = JSON.parse(localStorage.getItem('materiel') || '[]').map(item =>
            item.id_materiel === formData.id_materiel ? { ...item, etat: 'Attribué' } : item
        );

        localStorage.setItem('materiel', JSON.stringify(updatedEquipment));
        setAvailableEquipment(updatedEquipment.filter(item => item.etat === 'Disponible'));

        // Reset form
        setFormData({
            id_employee: '',
            id_materiel: '',
            date_attribution: new Date().toISOString().split('T')[0],
            date_retour: ''
        });
        setShowForm(false);
    };

    const handleDelete = (id_materiel) => {
        let updatedAssignments = assignments.filter(a => a.id_materiel !== id_materiel);
        setAssignments(updatedAssignments);
        localStorage.setItem('attributions', JSON.stringify(updatedAssignments));
        
        let updatedEquipment = JSON.parse(localStorage.getItem('materiel') || '[]').map(item =>
            item.id_materiel === id_materiel ? { ...item, etat: 'Disponible' } : item
        );
        localStorage.setItem('materiel', JSON.stringify(updatedEquipment));
        setAvailableEquipment(updatedEquipment.filter(item => item.etat === 'Disponible'));
    };

    return (
        <div>
            <h1>Equipment Assignments</h1>

            {/* New Attribution Button */}
            <button onClick={() => setShowForm(!showForm)}>
                {showForm ? "Cancel" : "New Attribution"}
            </button>

            {/* Inline Form for New Attribution */}
            {showForm && (
                <form onSubmit={handleSubmit} style={{ marginTop: '10px' }}>
                    <label>
                        Employee:
                        <select name="id_employee" value={formData.id_employee} onChange={handleChange} required>
                            <option value="">Select Employee</option>
                            {employees.map(emp => (
                                <option key={emp.id_employee} value={emp.id_employee}>{emp.name}</option>
                            ))}
                        </select>
                    </label>

                    <label>
                        Equipment:
                        <select name="id_materiel" value={formData.id_materiel} onChange={handleChange} required>
                            <option value="">Select Equipment</option>
                            {availableEquipment.map(item => (
                                <option key={item.id_materiel} value={item.id_materiel}>
                                    {item.type_materiel} - {item.marque}
                                </option>
                            ))}
                        </select>
                    </label>

                    <label>
                        Date Attribution:
                        <input type="date" name="date_attribution" value={formData.date_attribution} onChange={handleChange} required />
                    </label>

                    <label>
                        Return Date (Optional):
                        <input type="date" name="date_retour" value={formData.date_retour} onChange={handleChange} />
                    </label>

                    <button type="submit">Assign</button>
                </form>
            )}

            {/* Table of Existing Assignments */}
            <table style={{ marginTop: '20px' }}>
                <thead>
                    <tr>
                        <th>Employee</th>
                        <th>Equipment</th>
                        <th>Date Assigned</th>
                        <th>Return Date</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {assignments.map((a, index) => (
                        <tr key={index}>
                            <td>{a.employee_name}</td>
                            <td>{a.equipment_name}</td>
                            <td>{a.date_attribution}</td>
                            <td>{a.date_retour || 'N/A'}</td>
                            <td>
                                <button onClick={() => handleDelete(a.id_materiel)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Attributions;
